import React, { useState } from 'react';
import '../ui/Select.css';

export const Select = ({ children, defaultValue, onChange }) => {
  const [selectedValue, setSelectedValue] = useState(defaultValue || '');
  const [isOpen, setIsOpen] = useState(false);

  const handleSelectItem = (value) => {
    setSelectedValue(value);
    setIsOpen(false);
    if (onChange) {
      onChange(value);
    }
  };

  return (
    <div className="select">
      {React.Children.map(children, (child) =>
        React.cloneElement(child, {
          selectedValue,
          isOpen,
          onSelectItem: handleSelectItem,
          toggleOpen: () => setIsOpen((prev) => !prev),
        })
      )}
    </div>
  );
};

export const SelectTrigger = ({ children, toggleOpen, selectedValue }) => {
  return (
    <div className="select-trigger" onClick={toggleOpen}>
      <span className="select-value">{selectedValue || children}</span>
    </div>
  );
};

export const SelectContent = ({ children, isOpen }) => {
  return isOpen ? <div className="select-content">{children}</div> : null;
};

export const SelectItem = ({ children, value, onSelectItem }) => {
  return (
    <div className="select-item" onClick={() => onSelectItem(value || children)}>
      {children}
    </div>
  );
};

export const SelectValue = ({ children }) => {
  return <span className="select-value">{children}</span>;
};
