import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Chat from './components/Chat';
import Dashboard from './components/Dashboard';
import { AuthProvider, useAuth } from './context/AuthContext';

// PrivateRoute component using React Router v6 pattern
const PrivateRoute = ({ children }) => {
  const { user, loading } = useAuth();

  // Show loading state while checking authentication
  if (loading) {
    return <div>Loading...</div>;
  }

  // Redirect to login if not authenticated
  if (!user) {
    return <Navigate to="/login" replace />;
  }

  // Render children if authenticated
  return children;
};

// Component to handle role-based routing
const RoleRoute = ({ children, allowedRole }) => {
  const { user } = useAuth();

  if (user?.role !== allowedRole) {
    // Redirect to appropriate page based on role
    return <Navigate to={user?.role === 'client' ? '/dashboard' : '/chat'} replace />;
  }

  return children;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Public route - Login */}
          <Route path="/login" element={<Login />} />

          {/* Protected route - Chat (for customers) */}
          <Route
            path="/chat"
            element={
              <PrivateRoute>
                <RoleRoute allowedRole="customer">
                  <Chat />
                </RoleRoute>
              </PrivateRoute>
            }
          />

          {/* Protected route - Dashboard (for clients) */}
          <Route
            path="/dashboard"
            element={
              <PrivateRoute>
                <RoleRoute allowedRole="client">
                  <Dashboard />
                </RoleRoute>
              </PrivateRoute>
            }
          />

          {/* Root route - Redirect based on role */}
          <Route
            path="/"
            element={
              <PrivateRoute>
                {({ user }) => (
                  <Navigate 
                    to={user?.role === 'client' ? '/dashboard' : '/chat'} 
                    replace 
                  />
                )}
              </PrivateRoute>
            }
          />

          {/* Fallback route */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
