// src/components/ui/Dialog.js
import React, { useState } from 'react';
import './Dialog.css'; // Import the corresponding CSS

export const Dialog = ({ children }) => {
  return <div className="dialog">{children}</div>;
};

export const DialogTrigger = ({ children, onOpen }) => {
  return (
    <button className="dialog-trigger" onClick={onOpen}>
      {children}
    </button>
  );
};

export const DialogContent = ({ children, onClose }) => {
  return (
    <div className="dialog-content">
      <div className="dialog-overlay" onClick={onClose}></div>
      <div className="dialog-box">{children}</div>
    </div>
  );
};

export const DialogHeader = ({ children }) => {
  return <div className="dialog-header">{children}</div>;
};

export const DialogTitle = ({ children }) => {
  return <h2 className="dialog-title">{children}</h2>;
};

export const DialogDescription = ({ children }) => {
  return <p className="dialog-description">{children}</p>;
};
