import React, { useState } from 'react';
import '../ui/Select.css';

export const Select = ({ children, value, onChange }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleSelectItem = (newValue) => {
    setIsOpen(false);
    if (onChange) {
      onChange(newValue);
    }
  };

  const childrenWithProps = React.Children.map(children, child => {
    if (React.isValidElement(child)) {
      return React.cloneElement(child, {
        value,
        isOpen,
        onSelectItem: handleSelectItem,
        toggleOpen: () => setIsOpen(!isOpen)
      });
    }
    return child;
  });

  return <div className="select">{childrenWithProps}</div>;
};

export const SelectTrigger = ({ children, toggleOpen }) => {
  return (
    <div className="select-trigger" onClick={toggleOpen}>
      {children}
    </div>
  );
};

export const SelectContent = ({ children, isOpen, onSelectItem }) => {
  if (!isOpen) return null;

  return (
    <div className="select-content">
      {React.Children.map(children, child => {
        if (React.isValidElement(child)) {
          return React.cloneElement(child, {
            onSelectItem
          });
        }
        return child;
      })}
    </div>
  );
};

export const SelectItem = ({ children, value, onSelectItem }) => {
  const handleClick = () => {
    if (onSelectItem) {
      onSelectItem(value || children);
    }
  };

  return (
    <div className="select-item" onClick={handleClick}>
      {children}
    </div>
  );
};

export const SelectValue = ({ children }) => {
  return <span className="select-value">{children}</span>;
};
