import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import api from '../../services/api';
import CardContainer from '../common/CardContainer';
import Loader from '../common/Loader';
import NoDataMessage from '../common/NoDataMessage';
import { formatNumber, truncateText, createTooltip, debounce } from '../../utils/helpers';

const TopicDistributionChart = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartType, setChartType] = useState('bar'); // 'bar' or 'pie'
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const tooltipRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const topicsData = await api.getTopics();
        // Sort by count and take top 10
        const sortedData = topicsData
          .sort((a, b) => b.count - a.count)
          .slice(0, 10);
        setData(sortedData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching topics data:', err);
        setError('Failed to load topics data');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (data.length > 0 && !loading) {
      if (chartType === 'bar') {
        drawBarChart();
      } else {
        drawPieChart();
      }
      
      // Add resize listener
      const handleResize = debounce(() => {
        if (chartType === 'bar') {
          drawBarChart();
        } else {
          drawPieChart();
        }
      }, 300);
      
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }
  }, [data, loading, chartType]);

  const drawBarChart = () => {
    if (!svgRef.current || !containerRef.current) return;

    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();

    // Set dimensions
    const containerWidth = containerRef.current.clientWidth;
    const margin = { top: 20, right: 30, bottom: 100, left: 60 };
    const width = containerWidth - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;

    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Create tooltip if not already created
    if (!tooltipRef.current) {
      tooltipRef.current = createTooltip(d3);
    }

    // Create scales
    const xScale = d3.scaleBand()
      .domain(data.map(d => d.topic))
      .range([0, width])
      .padding(0.2);

    const yScale = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.count) * 1.1])
      .range([height, 0]);

    // Create axes
    const xAxis = d3.axisBottom(xScale);
    const yAxis = d3.axisLeft(yScale);

    // Add X axis
    svg.append('g')
      .attr('class', 'axis')
      .attr('transform', `translate(0,${height})`)
      .call(xAxis)
      .selectAll('text')
      .attr('transform', 'rotate(-45)')
      .style('text-anchor', 'end')
      .attr('dx', '-.8em')
      .attr('dy', '.15em');

    // Add Y axis
    svg.append('g')
      .attr('class', 'axis')
      .call(yAxis.tickFormat(d => formatNumber(d)))
      .append('text')
      .attr('class', 'axis-label')
      .attr('transform', 'rotate(-90)')
      .attr('y', -40)
      .attr('x', -height / 2)
      .attr('fill', 'var(--dark-text)')
      .attr('text-anchor', 'middle')
      .text('Number of Records');

    // Add grid lines
    svg.append('g')
      .attr('class', 'grid')
      .call(d3.axisLeft(yScale)
        .tickSize(-width)
        .tickFormat(''));

    // Add bars
    svg.selectAll('.bar')
      .data(data)
      .enter()
      .append('rect')
      .attr('class', 'bar')
      .attr('x', d => xScale(d.topic))
      .attr('y', d => yScale(d.count))
      .attr('width', xScale.bandwidth())
      .attr('height', d => height - yScale(d.count))
      .on('mouseover', function(event, d) {
        d3.select(this).attr('fill', 'var(--accent-color)');
        tooltipRef.current
          .style('opacity', 1)
          .html(`
            <strong>Topic:</strong> ${d.topic}<br>
            <strong>Count:</strong> ${formatNumber(d.count)}<br>
            <strong>Intensity:</strong> ${formatNumber(d.intensity)}<br>
            <strong>Likelihood:</strong> ${formatNumber(d.likelihood)}<br>
            <strong>Relevance:</strong> ${formatNumber(d.relevance)}
          `)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 20) + 'px');
      })
      .on('mouseout', function() {
        d3.select(this).attr('fill', 'var(--secondary-color)');
        tooltipRef.current.style('opacity', 0);
      });
  };

  const drawPieChart = () => {
    if (!svgRef.current || !containerRef.current) return;

    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();

    // Set dimensions
    const containerWidth = containerRef.current.clientWidth;
    const size = Math.min(containerWidth, 400);
    const margin = 40;
    const width = size - 2 * margin;
    const height = size - 2 * margin;
    const radius = Math.min(width, height) / 2;

    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('width', size)
      .attr('height', size)
      .append('g')
      .attr('transform', `translate(${size / 2},${size / 2})`);

    // Create tooltip if not already created
    if (!tooltipRef.current) {
      tooltipRef.current = createTooltip(d3);
    }

    // Create color scale
    const colorScale = d3.scaleOrdinal(d3.schemeCategory10);

    // Create pie generator
    const pie = d3.pie()
      .value(d => d.count)
      .sort(null);

    // Create arc generator
    const arc = d3.arc()
      .innerRadius(0)
      .outerRadius(radius);

    // Create outer arc for labels
    const outerArc = d3.arc()
      .innerRadius(radius * 1.1)
      .outerRadius(radius * 1.1);

    // Add slices
    const slices = svg.selectAll('.slice')
      .data(pie(data))
      .enter()
      .append('g')
      .attr('class', 'slice');

    slices.append('path')
      .attr('class', 'pie-slice')
      .attr('d', arc)
      .attr('fill', (d, i) => colorScale(i))
      .attr('stroke', 'white')
      .style('stroke-width', '2px')
      .on('mouseover', function(event, d) {
        d3.select(this).style('opacity', 0.8);
        tooltipRef.current
          .style('opacity', 1)
          .html(`
            <strong>Topic:</strong> ${d.data.topic}<br>
            <strong>Count:</strong> ${formatNumber(d.data.count)}<br>
            <strong>Percentage:</strong> ${(d.data.count / d3.sum(data, d => d.count) * 100).toFixed(1)}%<br>
            <strong>Intensity:</strong> ${formatNumber(d.data.intensity)}<br>
            <strong>Likelihood:</strong> ${formatNumber(d.data.likelihood)}
          `)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 20) + 'px');
      })
      .on('mouseout', function() {
        d3.select(this).style('opacity', 1);
        tooltipRef.current.style('opacity', 0);
      });

    // Add labels with lines
    slices.each(function(d) {
      if (d.endAngle - d.startAngle > 0.2) { // Only show labels for bigger slices
        const pos = outerArc.centroid(d);
        const midAngle = d.startAngle + (d.endAngle - d.startAngle) / 2;
        pos[0] = radius * 1.2 * (midAngle < Math.PI ? 1 : -1);

        const percent = (d.data.count / d3.sum(data, d => d.count) * 100).toFixed(0) + '%';
        
        // Add line
        d3.select(this).append('polyline')
          .attr('points', [
            arc.centroid(d),
            outerArc.centroid(d),
            pos
          ])
          .attr('stroke', 'var(--light-text)')
          .attr('fill', 'none')
          .attr('stroke-width', 1);
        
        // Add label
        d3.select(this).append('text')
          .attr('transform', `translate(${pos})`)
          .attr('dy', '.35em')
          .attr('text-anchor', midAngle < Math.PI ? 'start' : 'end')
          .attr('fill', 'var(--dark-text)')
          .attr('font-size', '10px')
          .text(`${truncateText(d.data.topic, 12)} (${percent})`);
      }
    });
  };

  const renderActionButtons = () => (
    <div className="chart-actions">
      <button
        className={`chart-btn ${chartType === 'bar' ? 'active' : ''}`}
        onClick={() => setChartType('bar')}
      >
        Bar
      </button>
      <button
        className={`chart-btn ${chartType === 'pie' ? 'active' : ''}`}
        onClick={() => setChartType('pie')}
      >
        Pie
      </button>
    </div>
  );

  return (
    <CardContainer 
      title="Topic Distribution" 
      actionButtons={renderActionButtons()}
    >
      <div ref={containerRef} style={{ width: '100%', height: '400px' }}>
        {loading ? (
          <Loader message="Loading topic data..." />
        ) : error ? (
          <NoDataMessage message={error} icon="⚠️" />
        ) : data.length === 0 ? (
          <NoDataMessage message="No topic data available" />
        ) : (
          <svg ref={svgRef} className="chart-svg"></svg>
        )}
      </div>
    </CardContainer>
  );
};

export default TopicDistributionChart;
