import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Chat from './components/Chat';
import { AuthProvider, useAuth } from './context/AuthContext';

// PrivateRoute to protect routes that require authentication
function PrivateRoute({ element, ...rest }) {
  const { authState } = useAuth();

  // If the user is not authenticated, redirect to the login page
  if (!authState.isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // If authenticated, return the requested element
  return element;
}

// The main App component
function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Login Route - Allowing user to log in */}
          <Route path="/login" element={<Login />} />

          {/* PrivateRoute - Protecting the chat route */}
          <Route
            path="/chat"
            element={
              <PrivateRoute element={<Chat />} />
            }
          />

          {/* Redirect the root path to login */}
          <Route path="/" element={<Navigate to="/login" replace />} />

          {/* Fallback Route */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
