import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Chat from './components/Chat';
import { AuthProvider, useAuth } from './context/AuthContext';

// PrivateRoute to protect routes that require authentication
function PrivateRoute({ component: Component, ...rest }) {
  const { authState } = useAuth();

  return (
    <Route
      {...rest}
      render={(props) =>
        authState.isAuthenticated ? (
          <Component {...props} />
        ) : (
          <Redirect to="/login" />
        )
      }
    />
  );
}

// The main App component
function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Login Route - Allowing user to log in */}
          <Route path="/login" component={Login} />

          {/* PrivateRoute - Protecting the chat route */}
          <PrivateRoute path="/chat" component={Chat} />

          {/* Redirect the root path to login */}
          <Navigate exact from="/" to="/login" />

          {/* Fallback Route */}
          <Route render={() => <Navigate to="/login" />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
