// src/components/ui/Label.js
import React from 'react';
import './Label.css'; // Import the corresponding CSS

export const Label = ({ htmlFor, children, className = '' }) => {
  return (
    <label htmlFor={htmlFor} className={`label ${className}`}>
      {children}
    </label>
  );
};
