import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { sendChatMessage } from '../api/chatApi';
import './Chat.css';

const Chat = () => {
  const { user } = useAuth();  // Changed from authState to user to match AuthContext
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSendMessage = async () => {
    if (!message.trim()) {
      setError('Please enter a message.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const result = await sendChatMessage(
        message, 
        user?.customerId || 'guest',  // Use customerId from user object
        user?.role || 'guest'
      );

      if (result.error) {
        setError(result.error);
      } else {
        setChatHistory((prev) => [
          ...prev,
          { sender: 'user', message },
          { sender: 'bot', message: result.response },
        ]);

        setProperties(result.properties || []);
      }
    } catch {
      setError('An error occurred while sending your message.');
    } finally {
      setLoading(false);
      setMessage('');
    }
  };

  return (
    <div className="chat-container">
      <h1 className="chat-header">Chat with our Assistant</h1>

      {error && <p className="chat-error">{error}</p>}

      <div className="chat-history">
        {chatHistory.map((chat, index) => (
          <div key={index} className={`chat-bubble ${chat.sender}`}>
            <p>{chat.message}</p>
          </div>
        ))}
      </div>

      <div className="chat-input-area">
        <textarea
          className="chat-input"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button 
          className="chat-send-btn" 
          onClick={handleSendMessage} 
          disabled={loading}
        >
          {loading ? 'Sending...' : 'Send'}
        </button>
      </div>

      {properties.length > 0 && (
        <div className="properties-suggested">
          <h2>Properties Suggested</h2>
          <div className="properties-list">
            {properties.map((property, index) => (
              <div key={index} className="property-card">
                <h3>{property.title}</h3>
                <p><strong>Price:</strong> ${property.price}</p>
                <p><strong>Description:</strong> {property.description}</p>
                {property.images && property.images.length > 0 && (
                  <div className="property-images">
                    <img
                      src={property.images[0]}
                      alt={property.title}
                      className="property-image"
                    />
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
