import React, { useState } from 'react';
import { Bell } from 'lucide-react';
import { Tabs, TabsList, TabsTrigger, TabsContent } from '../components/ui/Tabs';
import { Label, Input } from '../components/ui/Form';
import { Alert, AlertDescription } from '../components/ui/alert';
import DashboardOverview from './DashboardOverview';
import CustomersManagement from './CustomersManagement';
import TicketsManagement from './TicketsManagement';
import OverallAnalytics from './OverallAnalytics';
import CustomerAnalytics from './CustomerAnalytics';

const API_BASE_URL = 'http://localhost:5000/api';

const Dashboard = () => {
  const [customerEmail, setCustomerEmail] = useState('');
  const [analyticsView, setAnalyticsView] = useState('overall');
  const [customerId, setCustomerId] = useState(null);
  const [error, setError] = useState(null);

  const handleEmailSearch = async (e) => {
    const email = e.target.value;
    setCustomerEmail(email);
    setError(null);

    if (!email) {
      setCustomerId(null);
      setAnalyticsView('overall');
      return;
    }

    // Only search if email is valid
    if (email && /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      try {
        const response = await fetch(`${API_BASE_URL}/api/customers/search?email=${encodeURIComponent(email)}`);
        const data = await response.json();
        
        if (!response.ok) {
          throw new Error(data.error || 'Customer not found');
        }

        if (data.customerId) {
          setCustomerId(data.customerId);
          setAnalyticsView('customer'); // Automatically switch to customer view when we find a match
        } else {
          setCustomerId(null);
          setError('No customer found with this email');
          setAnalyticsView('overall');
        }
      } catch (err) {
        setError(err.message);
        setCustomerId(null);
        setAnalyticsView('overall');
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-gray-900">CRM Dashboard</h1>
          <div className="flex items-center space-x-4">
            <Bell className="h-6 w-6 text-gray-500" />
            <div className="h-8 w-8 rounded-full bg-gray-300" />
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 py-6">
        <Tabs defaultValue="overview" className="space-y-4">
          <TabsList>
            <TabsTrigger value="overview">Overview</TabsTrigger>
            <TabsTrigger value="customers">Customers</TabsTrigger>
            <TabsTrigger value="tickets">Tickets</TabsTrigger>
            <TabsTrigger value="analytics">Analytics</TabsTrigger>
          </TabsList>

          <TabsContent value="overview" contentValue="overview">
            <DashboardOverview />
          </TabsContent>

          <TabsContent value="customers" contentValue="customers">
            <CustomersManagement />
          </TabsContent>

          <TabsContent value="tickets" contentValue="tickets">
            <TicketsManagement />
          </TabsContent>

          <TabsContent value="analytics" contentValue="analytics">
            <div className="space-y-6">
              <div className="flex flex-col space-y-2">
                <Label htmlFor="customer-email">Customer Analytics</Label>
                <div className="flex items-center space-x-4">
                  <Input
                    id="customer-email"
                    type="email"
                    placeholder="Enter Customer Email"
                    value={customerEmail}
                    onChange={handleEmailSearch}
                    className="max-w-xs"
                  />
                </div>
                {error && (
                  <Alert variant="destructive">
                    <AlertDescription>{error}</AlertDescription>
                  </Alert>
                )}
              </div>

              <Tabs value={analyticsView} onValueChange={setAnalyticsView}>
                <TabsList className="space-x-2">
                  <TabsTrigger value="overall">Overall Analytics</TabsTrigger>
                  {customerId && (
                    <TabsTrigger value="customer">Customer Analytics</TabsTrigger>
                  )}
                </TabsList>

                <TabsContent value="overall" contentValue="overall">
                  <OverallAnalytics />
                </TabsContent>

                <TabsContent value="customer" contentValue="overall">
                  {customerId && (
                    <CustomerAnalytics customerId={customerId} />
                  )}
                </TabsContent>
              </Tabs>
            </div>
          </TabsContent>
        </Tabs>
      </main>
    </div>
  );
};

export default Dashboard;
