import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Chat from './components/Chat';
import Dashboard from './components/Dashboard'; // Assuming Dashboard component exists
import { AuthProvider, useAuth } from './context/AuthContext';

// PrivateRoute component using React Router v6 pattern
const PrivateRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return children;
};

// Role-based route protection
const RoleRoute = ({ children, allowedRole, redirectTo }) => {
  const { user } = useAuth();

  if (user?.role !== allowedRole) {
    return <Navigate to={redirectTo} replace />;
  }

  return children;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Login route - always accessible */}
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<Navigate to="/login" replace />} />

          {/* Protected route - Chat (for customers) */}
          <Route
            path="/chat"
            element={
              <PrivateRoute>
                <RoleRoute allowedRole="customer" redirectTo="/dashboard">
                  <Chat />
                </RoleRoute>
              </PrivateRoute>
            }
          />

          {/* Protected route - Dashboard (for clients) */}
          <Route
            path="/dashboard"
            element={
              <PrivateRoute>
                <RoleRoute allowedRole="client" redirectTo="/chat">
                  <Dashboard />
                </RoleRoute>
              </PrivateRoute>
            }
          />

          {/* Fallback route for invalid URLs */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
