import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { sendChatMessage } from '../api/chatApi';
import { Alert, AlertDescription } from '../../src/components/ui/alert';
import { Button } from '../../src/components/ui/Button';
import { Input } from '../../src/components/ui/Input';
import { Card, CardContent } from '../../src/components/ui/Card';
import { Badge } from '../../src/components/ui/Badge';
import { Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious } from '../../src/components/ui/Carousel';

const PropertyDisplay = ({ property }) => {
  if (!property) return null;

  return (
    <div className="border rounded-lg p-4 mt-2 bg-white shadow-sm">
      {property.images && property.images.length > 0 && (
        <Carousel className="w-full max-w-lg mx-auto">
          <CarouselContent>
            {property.images.map((image, index) => (
              <CarouselItem key={index}>
                <img 
                  src={image} 
                  alt={`Property view ${index + 1}`}
                  className="w-full h-48 object-cover rounded"
                />
              </CarouselItem>
            ))}
          </CarouselContent>
          <CarouselPrevious />
          <CarouselNext />
        </Carousel>
      )}
      
      <div className="mt-4">
        {property.prices && (
          <div className="flex gap-2 mb-2">
            {Object.entries(property.prices).map(([type, price]) => (
              <Badge key={type} variant="secondary">
                {type}: ${parseFloat(price).toLocaleString()}
              </Badge>
            ))}
          </div>
        )}
        
        {property.description && (
          <p className="text-sm text-gray-600 mt-2">{property.description}</p>
        )}

        {/* Display additional property details if available */}
        {property.amenities && (
          <div className="mt-2">
            <h4 className="text-sm font-semibold">Amenities:</h4>
            <div className="flex flex-wrap gap-2 mt-1">
              {property.amenities.map((amenity, index) => (
                <Badge key={index} variant="outline">
                  {amenity}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {property.location && (
          <p className="text-sm text-gray-600 mt-2">
            Location: {property.location}
          </p>
        )}
      </div>
    </div>
  );
};

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const messagesEndRef = useRef(null);
  const { user } = useAuth();
  const navigate = useNavigate();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim()) return;

    setLoading(true);
    setError('');

    const newMessage = {
      content: inputMessage,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, newMessage]);
    setInputMessage('');

    try {
      const response = await sendChatMessage(inputMessage, user.customerId, user.role);

      if (response.error) {
        throw new Error(response.error);
      }

      // Add bot response with property information if available
      const botMessage = {
        content: response.response,
        sender: 'bot',
        timestamp: new Date().toISOString(),
        properties: response.properties  // Direct property data from chatbot
      };

      setMessages(prev => [...prev, botMessage]);

      // If a ticket was created, show notification
      if (response.ticket) {
        setMessages(prev => [...prev, {
          content: `Ticket #${response.ticket.ticket_id} has been created for your request.`,
          sender: 'system',
          timestamp: new Date().toISOString(),
          ticket: response.ticket
        }]);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleViewTicket = (ticketId) => {
    navigate(`/tickets/${ticketId}`);
  };

  return (
    <div className="flex flex-col h-screen max-w-3xl mx-auto p-4">
      <Card className="flex-grow mb-4 overflow-hidden">
        <CardContent className="h-full overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${
                message.sender === 'user' ? 'justify-end' : 'justify-start'
              }`}
            >
              <div
                className={`max-w-[80%] p-3 rounded-lg ${
                  message.sender === 'user'
                    ? 'bg-blue-500 text-white'
                    : message.sender === 'system'
                    ? 'bg-gray-200 text-gray-800'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                <p className="whitespace-pre-wrap">{message.content}</p>
                
                {/* Display properties if available in bot response */}
                {message.properties && message.properties.map((property, idx) => (
                  <PropertyDisplay 
                    key={idx}
                    property={property}
                  />
                ))}

                {message.ticket && (
                  <Button
                    variant="outline"
                    size="sm"
                    className="mt-2"
                    onClick={() => handleViewTicket(message.ticket.ticket_id)}
                  >
                    View Ticket
                  </Button>
                )}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </CardContent>
      </Card>

      {error && (
        <Alert variant="destructive" className="mb-4">
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <form onSubmit={handleSendMessage} className="flex gap-2">
        <Input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Type your message..."
          disabled={loading}
          className="flex-grow"
        />
        <Button type="submit" disabled={loading}>
          {loading ? 'Sending...' : 'Send'}
        </Button>
      </form>
    </div>
  );
};

export default Chat;
