import React, { useState } from 'react';
import './Tabs.css'; // Make sure this CSS file exists and styles your tabs properly

export const Tabs = ({ children }) => {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  const tabsList = React.Children.toArray(children).filter(child => child.type.name === 'TabsList');
  const tabsContent = React.Children.toArray(children).filter(child => child.type.name === 'TabsContent');

  return (
    <div className="tabs">
      {tabsList.map((tabList, index) => 
        React.cloneElement(tabList, { activeTab, onTabClick: handleTabClick, key: index })
      )}
      {tabsContent.map((content, index) =>
        React.cloneElement(content, { isActive: activeTab === index, key: index })
      )}
    </div>
  );
};

export const TabsList = ({ children, activeTab, onTabClick }) => {
  return (
    <div className="tabs-list">
      {React.Children.map(children, (child, index) =>
        React.cloneElement(child, { isActive: activeTab === index, onClick: () => onTabClick(index) })
      )}
    </div>
  );
};

export const TabsTrigger = ({ children, isActive, onClick }) => {
  return (
    <button className={`tabs-trigger ${isActive ? 'active' : ''}`} onClick={onClick}>
      {children}
    </button>
  );
};

export const TabsContent = ({ children, isActive }) => {
  return (
    isActive && <div className="tabs-content">{children}</div>
  );
};
