import React, { useState, useEffect } from 'react';
import { Plus, Edit2, Trash2 } from 'lucide-react';
import { Button } from '../components/ui/Button'; // Custom Button Component
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../components/ui/Dialog'; // Custom Dialog Components
import { Table, TableHeader, TableRow, TableHead, TableBody, TableCell } from '../components/ui/Table'; // Custom Table Components
import CustomerForm from './CustomerForm'; // Custom Customer Form Component

const API_BASE_URL = 'http://localhost:5000/api';

const CustomersManagement = () => {
  const [customers, setCustomers] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  useEffect(() => {
    fetchCustomers();

    // Ensure the dialog is closed on component mount and during cleanup
    return () => {
      setIsDialogOpen(false);
    };
  }, []);

  const fetchCustomers = () => {
    fetch(`${API_BASE_URL}/customers`)
      .then((res) => res.json())
      .then((data) => setCustomers(data))
      .catch((err) => console.error('Error fetching customers:', err));
  };

  const handleCreateCustomer = (data) => {
    fetch(`${API_BASE_URL}/customers`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    })
      .then(() => {
        fetchCustomers();
        closeDialog();
      })
      .catch((err) => console.error('Error creating customer:', err));
  };

  const handleUpdateCustomer = (data) => {
    fetch(`${API_BASE_URL}/customers/${selectedCustomer.customer_id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    })
      .then(() => {
        fetchCustomers();
        closeDialog();
        setSelectedCustomer(null);
      })
      .catch((err) => console.error('Error updating customer:', err));
  };

  const handleDeleteCustomer = (customerId) => {
    if (window.confirm('Are you sure you want to delete this customer?')) {
      fetch(`${API_BASE_URL}/customers/${customerId}`, {
        method: 'DELETE',
      })
        .then(() => fetchCustomers())
        .catch((err) => console.error('Error deleting customer:', err));
    }
  };

  const closeDialog = () => {
    setIsDialogOpen(false);
    setSelectedCustomer(null);
  };

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Customers</h2>
        <Button
          onClick={() => {
            setSelectedCustomer(null);
            setIsDialogOpen(true);
          }}
        >
          <Plus className="h-4 w-4 mr-2" />
          Add Customer
        </Button>
      </div>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{selectedCustomer ? 'Edit Customer' : 'Add Customer'}</DialogTitle>
          </DialogHeader>
          <CustomerForm
            customer={selectedCustomer}
            onSubmit={selectedCustomer ? handleUpdateCustomer : handleCreateCustomer}
            onClose={closeDialog}
          />
        </DialogContent>
      </Dialog>

      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Name</TableHead>
            <TableHead>Email</TableHead>
            <TableHead>Preferences</TableHead>
            <TableHead>Actions</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {customers.map((customer) => (
            <TableRow key={customer.customer_id}>
              <TableCell>{customer.name}</TableCell>
              <TableCell>{customer.email}</TableCell>
              <TableCell>{customer.preferences}</TableCell>
              <TableCell>
                <div className="flex space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      setSelectedCustomer(customer);
                      setIsDialogOpen(true);
                    }}
                  >
                    <Edit2 className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => handleDeleteCustomer(customer.customer_id)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default CustomersManagement;
