import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import api from '../../services/api';
import CardContainer from '../common/CardContainer';
import Loader from '../common/Loader';
import NoDataMessage from '../common/NoDataMessage';
import { formatNumber, createTooltip } from '../../utils/helpers';

const IntensityChart = ({ data: propData, fullSize = false }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortOrder, setSortOrder] = useState('desc'); // 'asc' or 'desc'
  const svgRef = useRef(null);
  const tooltipRef = useRef(null);
  
  useEffect(() => {
    // Use provided data if available, otherwise fetch from API
    if (propData) {
      setData(propData);
      setLoading(false);
    } else {
      const fetchData = async () => {
        try {
          setLoading(true);
          const result = await api.getTopN('intensity', 'country', 10);
          setData(result);
          setLoading(false);
        } catch (err) {
          setError('Failed to load intensity data');
          setLoading(false);
          console.error(err);
        }
      };
      
      fetchData();
    }
  }, [propData]);
  
  useEffect(() => {
    if (data.length > 0 && svgRef.current) {
      drawChart();
    }
  }, [data, sortOrder]);
  
  const sortData = () => {
    const newSortOrder = sortOrder === 'desc' ? 'asc' : 'desc';
    setSortOrder(newSortOrder);
    
    // Create a copy of the data and sort it
    const sortedData = [...data].sort((a, b) => {
      return newSortOrder === 'asc' 
        ? a.value - b.value 
        : b.value - a.value;
    });
    
    setData(sortedData);
  };
  
  const drawChart = () => {
    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();
    
    // Chart dimensions
    const margin = { top: 20, right: 30, bottom: 60, left: 70 };
    const width = svgRef.current.clientWidth - margin.left - margin.right;
    const height = fullSize ? 450 : 350 - margin.top - margin.bottom;
    
    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);
    
    // Create tooltip if it doesn't exist
    if (!tooltipRef.current) {
      tooltipRef.current = createTooltip(d3);
    }
    
    // Create gradient for bars
    const defs = svg.append('defs');
    
    const gradient = defs.append('linearGradient')
      .attr('id', 'bar-gradient')
      .attr('gradientUnits', 'userSpaceOnUse')
      .attr('x1', 0)
      .attr('y1', height)
      .attr('x2', 0)
      .attr('y2', 0);
    
    gradient.append('stop')
      .attr('offset', '0%')
      .attr('stop-color', '#4682B4');
    
    gradient.append('stop')
      .attr('offset', '100%')
      .attr('stop-color', '#1E3B5A');
    
    // Scales
    const x = d3.scaleBand()
      .domain(data.map(d => d.name))
      .range([0, width])
      .padding(0.3);
    
    const y = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.value) * 1.1])
      .range([height, 0]);
    
    // Add background grid
    svg.append('g')
      .attr('class', 'grid')
      .attr('opacity', 0.1)
      .call(d3.axisLeft(y)
        .tickSize(-width)
        .tickFormat(''));
    
    // Add a subtle background to the chart area
    svg.append('rect')
      .attr('width', width)
      .attr('height', height)
      .attr('fill', '#f8f9fa')
      .attr('opacity', 0.3)
      .attr('rx', 5)
      .attr('ry', 5);
    
    // Axes
    svg.append('g')
      .attr('class', 'axis x-axis')
      .attr('transform', `translate(0,${height})`)
      .call(d3.axisBottom(x))
      .selectAll('text')
      .attr('transform', 'rotate(-40)')
      .style('text-anchor', 'end')
      .attr('dx', '-.8em')
      .attr('dy', '.15em')
      .attr('font-size', '11px')
      .attr('fill', '#555');
    
    svg.append('g')
      .attr('class', 'axis y-axis')
      .call(d3.axisLeft(y).ticks(5))
      .selectAll('text')
      .attr('font-size', '11px')
      .attr('fill', '#555');
    
    // Enhanced styling for axes
    svg.selectAll('.axis line, .axis path')
      .attr('stroke', '#ccc')
      .attr('stroke-width', 1);
    
    // Animated bars with gradient
    svg.selectAll('.bar')
      .data(data)
      .enter()
      .append('rect')
      .attr('class', 'bar')
      .attr('rx', 3)
      .attr('ry', 3)
      .attr('x', d => x(d.name))
      .attr('width', x.bandwidth())
      .attr('y', height) // Start from the bottom for animation
      .attr('height', 0)
      .attr('fill', 'url(#bar-gradient)')
      .attr('stroke', '#1E3B5A')
      .attr('stroke-width', 1)
      .attr('opacity', 0.9)
      .on('mouseover', function(event, d) {
        // Highlight bar on hover
        d3.select(this)
          .attr('opacity', 1)
          .attr('stroke-width', 2);
          
        tooltipRef.current.transition()
          .duration(200)
          .style('opacity', 0.9);
        
        tooltipRef.current.html(`
          <div style="background: rgba(30, 59, 90, 0.95); color: white; padding: 8px 12px; border-radius: 4px; box-shadow: 0 4px 8px rgba(0,0,0,0.2);">
            <div style="font-weight: bold; font-size: 14px; margin-bottom: 4px;">${d.name}</div>
            <div>Intensity: <strong>${formatNumber(d.value)}</strong></div>
            <div>Count: <strong>${d.count}</strong></div>
          </div>
        `)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 28) + 'px');
      })
      .on('mouseout', function() {
        d3.select(this)
          .attr('opacity', 0.9)
          .attr('stroke-width', 1);
          
        tooltipRef.current.transition()
          .duration(500)
          .style('opacity', 0);
      })
      .transition() // Animate the bars
      .duration(800)
      .delay((d, i) => i * 100)
      .ease(d3.easeElastic.amplitude(0.5))
      .attr('y', d => y(d.value))
      .attr('height', d => height - y(d.value));
    
    // Value labels on top of bars
    svg.selectAll('.bar-label')
      .data(data)
      .enter()
      .append('text')
      .attr('class', 'bar-label')
      .attr('x', d => x(d.name) + x.bandwidth() / 2)
      .attr('y', d => y(d.value) - 5)
      .attr('text-anchor', 'middle')
      .attr('font-size', '11px')
      .attr('fill', '#1E3B5A')
      .attr('opacity', 0)
      .text(d => formatNumber(d.value))
      .transition()
      .duration(800)
      .delay((d, i) => i * 100 + 300)
      .attr('opacity', 1);
    
    // Axes labels with better styling
    svg.append('text')
      .attr('class', 'axis-label x-label')
      .attr('text-anchor', 'middle')
      .attr('x', width / 2)
      .attr('y', height + margin.bottom - 10)
      .attr('fill', '#333')
      .attr('font-size', '12px')
      .attr('font-weight', 'bold')
      .text('Country');
    
    svg.append('text')
      .attr('class', 'axis-label y-label')
      .attr('text-anchor', 'middle')
      .attr('transform', 'rotate(-90)')
      .attr('x', -height / 2)
      .attr('y', -margin.left + 15)
      .attr('fill', '#333')
      .attr('font-size', '12px')
      .attr('font-weight', 'bold')
      .text('Average Intensity');
  };
  
  // Resize handler
  useEffect(() => {
    const handleResize = () => {
      if (data.length > 0) {
        drawChart();
      }
    };
    
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      // Clean up tooltip when component unmounts
      if (tooltipRef.current) {
        d3.select('.d3-tooltip').remove();
        tooltipRef.current = null;
      }
    };
  }, [data]);
  
  const actionButtons = (
    <>
      <button 
        className="sort-btn" 
        onClick={sortData}
        title={`Sort ${sortOrder === 'desc' ? 'ascending' : 'descending'}`}
        style={{
          marginRight: '10px',
          padding: '4px 8px',
          backgroundColor: '#f8f9fa',
          border: '1px solid #ddd',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        {sortOrder === 'desc' ? '↑ Sort' : '↓ Sort'}
      </button>
      
      <button 
        className="refresh-btn" 
        onClick={() => {
          setLoading(true);
          api.getTopN('intensity', 'country', 10)
            .then(result => {
              setData(result);
              setLoading(false);
            })
            .catch(err => {
              setError('Failed to refresh data');
              setLoading(false);
              console.error(err);
            });
        }}
        style={{
          padding: '4px 8px',
          backgroundColor: '#f8f9fa',
          border: '1px solid #ddd',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        ↻ Refresh
      </button>
    </>
  );
  
  // If this component is being used as a child in another component
  if (propData) {
    return (
      <div className="intensity-chart-container">
        {loading ? (
          <Loader message="Loading intensity data..." />
        ) : error ? (
          <NoDataMessage message={error} icon="⚠️" />
        ) : data.length === 0 ? (
          <NoDataMessage message="No intensity data available" />
        ) : (
          <svg ref={svgRef} className="chart-svg" style={{ width: '100%' }}></svg>
        )}
      </div>
    );
  }
  
  // Standalone usage with its own card container
  return (
    <CardContainer title="Top Countries by Intensity" actionButtons={actionButtons}>
      {loading ? (
        <Loader message="Loading intensity data..." />
      ) : error ? (
        <NoDataMessage message={error} icon="⚠️" />
      ) : data.length === 0 ? (
        <NoDataMessage message="No intensity data available" />
      ) : (
        <svg ref={svgRef} className="chart-svg" style={{ width: '100%' }}></svg>
      )}
    </CardContainer>
  );
};

export default IntensityChart;
