import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Chat from './components/Chat';
import Dashboard from './components/Dashboard';
import { AuthProvider, useAuth } from './context/AuthContext';

// PrivateRoute component using React Router v6 pattern
const PrivateRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return children;
};

// Component to handle role-based routing
const RoleRoute = ({ children, allowedRole }) => {
  const { user } = useAuth();

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  if (user.role !== allowedRole) {
    return <Navigate to={user.role === 'client' ? '/dashboard' : '/chat'} replace />;
  }

  return children;
};

// Root redirect component
const RootRedirect = () => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <Navigate to={user.role === 'client' ? '/dashboard' : '/chat'} replace />;
};

// Public route wrapper to prevent signed-in users from accessing login
const PublicRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div>Loading...</div>;
  }

  if (user) {
    return <Navigate to={user.role === 'client' ? '/dashboard' : '/chat'} replace />;
  }

  return children;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Public route - Login with protection from accessing when signed in */}
          <Route
            path="/login"
            element={
              <PublicRoute>
                <Login />
              </PublicRoute>
            }
          />

          {/* Protected route - Chat (for customers) */}
          <Route
            path="/chat"
            element={
              <PrivateRoute>
                <RoleRoute allowedRole="customer">
                  <Chat />
                </RoleRoute>
              </PrivateRoute>
            }
          />

          {/* Protected route - Dashboard (for clients) */}
          <Route
            path="/dashboard"
            element={
              <PrivateRoute>
                <RoleRoute allowedRole="client">
                  <Dashboard />
                </RoleRoute>
              </PrivateRoute>
            }
          />

          {/* Root route - Redirect based on auth state and role */}
          <Route
            path="/"
            element={<RootRedirect />}
          />

          {/* Fallback route */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
