import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';
import api from '../../services/api';
import CardContainer from '../common/CardContainer';
import Loader from '../common/Loader';
import NoDataMessage from '../common/NoDataMessage';
import { createTooltip, generateCategoryColors, formatNumber } from '../../utils/helpers';

const RegionChart = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartType, setChartType] = useState('pie'); // 'pie' or 'bar'
  const svgRef = useRef(null);
  const legendRef = useRef(null);
  
  useEffect(() => {
    const fetchRegionData = async () => {
      try {
        setLoading(true);
        const regionData = await api.getRegions();
        setData(regionData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching region data:', err);
        setError('Failed to load region data');
        setLoading(false);
      }
    };
    
    fetchRegionData();
  }, []);
  
  useEffect(() => {
    if (data && data.length > 0 && !loading) {
      renderChart();
    }
  }, [data, loading, chartType]);
  
  useEffect(() => {
    // Add resize listener
    const handleResize = () => {
      if (data && data.length > 0 && !loading) {
        renderChart();
      }
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [data, loading, chartType]);
  
  const renderChart = () => {
    if (!svgRef.current) return;
    
    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();
    d3.select(legendRef.current).selectAll('*').remove();
    
    const margin = { top: 20, right: 20, bottom: chartType === 'bar' ? 100 : 30, left: chartType === 'bar' ? 60 : 30 };
    const width = svgRef.current.clientWidth - margin.left - margin.right;
    const height = 350 - margin.top - margin.bottom;
    
    // Process and sort data
    const processedData = [...data]
      .filter(d => d.region && d.region !== 'Unknown' && d.count > 0)
      .sort((a, b) => b.count - a.count);
    
    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('class', 'chart-svg')
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom);
    
    // Create tooltip
    const tooltip = createTooltip(d3);
    
    if (chartType === 'pie') {
      renderPieChart(svg, processedData, width, height, margin, tooltip);
    } else {
      renderBarChart(svg, processedData, width, height, margin, tooltip);
    }
  };
  
  const renderPieChart = (svg, data, width, height, margin, tooltip) => {
    const g = svg.append('g')
      .attr('transform', `translate(${width / 2 + margin.left}, ${height / 2 + margin.top})`);
    
    // Calculate total for percentages
    const total = data.reduce((sum, d) => sum + d.count, 0);
    
    // Create color scale
    const color = d3.scaleOrdinal()
      .domain(data.map(d => d.region))
      .range(d3.schemeCategory10);
    
    // Create pie generator
    const pie = d3.pie()
      .value(d => d.count)
      .sort(null);
    
    // Create arc generator
    const radius = Math.min(width, height) / 2;
    const arc = d3.arc()
      .innerRadius(0)
      .outerRadius(radius * 0.8);
    
    // Create outer arc for labels
    const outerArc = d3.arc()
      .innerRadius(radius * 0.9)
      .outerRadius(radius * 0.9);
    
    // Draw pie slices
    g.selectAll('path')
      .data(pie(data))
      .enter()
      .append('path')
      .attr('d', arc)
      .attr('fill', d => color(d.data.region))
      .attr('class', 'pie-slice')
      .attr('stroke', 'white')
      .attr('stroke-width', 2)
      .on('mouseover', function(event, d) {
        const percentage = (d.data.count / total * 100).toFixed(1);
        
        tooltip.transition()
          .duration(200)
          .style('opacity', 0.9);
        tooltip.html(`<strong>${d.data.region}</strong><br/>
                     Count: ${formatNumber(d.data.count)}<br/>
                     Percentage: ${percentage}%`)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 28) + 'px');
        
        d3.select(this)
          .attr('opacity', 0.7);
      })
      .on('mouseout', function() {
        tooltip.transition()
          .duration(500)
          .style('opacity', 0);
        
        d3.select(this)
          .attr('opacity', 1);
      });
    
    // Create legend
    const legend = d3.select(legendRef.current)
      .attr('class', 'chart-legend');
    
    data.forEach((item, index) => {
      const percentage = (item.count / total * 100).toFixed(1);
      
      const legendItem = legend.append('div')
        .attr('class', 'legend-item')
        .style('cursor', 'pointer')
        .on('mouseover', function() {
          g.selectAll('.pie-slice')
            .filter((d, i) => i !== index)
            .attr('opacity', 0.3);
        })
        .on('mouseout', function() {
          g.selectAll('.pie-slice')
            .attr('opacity', 1);
        });
      
      legendItem.append('div')
        .attr('class', 'legend-color')
        .style('background-color', color(item.region));
      
      legendItem.append('div')
        .text(`${item.region} (${percentage}%)`);
    });
  };
  
  const renderBarChart = (svg, data, width, height, margin, tooltip) => {
    const g = svg.append('g')
      .attr('transform', `translate(${margin.left}, ${margin.top})`);
    
    // Create scales
    const x = d3.scaleBand()
      .domain(data.map(d => d.region))
      .range([0, width])
      .padding(0.2);
    
    const y = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.count) * 1.1])
      .nice()
      .range([height, 0]);
    
    // Create axes
    const xAxis = g.append('g')
      .attr('class', 'axis')
      .attr('transform', `translate(0, ${height})`)
      .call(d3.axisBottom(x))
      .selectAll('text')
      .attr('transform', 'rotate(-45)')
      .attr('text-anchor', 'end')
      .attr('dx', '-.8em')
      .attr('dy', '.15em');
    
    const yAxis = g.append('g')
      .attr('class', 'axis')
      .call(d3.axisLeft(y).ticks(5));
    
    // Create grid
    g.append('g')
      .attr('class', 'grid')
      .call(d3.axisLeft(y)
        .tickSize(-width)
        .tickFormat('')
      );
    
    // Draw bars
    g.selectAll('.bar')
      .data(data)
      .enter()
      .append('rect')
      .attr('class', 'bar')
      .attr('x', d => x(d.region))
      .attr('width', x.bandwidth())
      .attr('y', d => y(d.count))
      .attr('height', d => height - y(d.count))
      .on('mouseover', function(event, d) {
        tooltip.transition()
          .duration(200)
          .style('opacity', 0.9);
        tooltip.html(`<strong>${d.region}</strong><br/>Count: ${formatNumber(d.count)}`)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 28) + 'px');
        
        d3.select(this)
          .attr('opacity', 0.7);
      })
      .on('mouseout', function() {
        tooltip.transition()
          .duration(500)
          .style('opacity', 0);
        
        d3.select(this)
          .attr('opacity', 1);
      });
    
    // Add Y axis label
    g.append('text')
      .attr('transform', 'rotate(-90)')
      .attr('y', -margin.left + 15)
      .attr('x', -height / 2)
      .attr('dy', '1em')
      .style('text-anchor', 'middle')
      .text('Number of Events');
    
    // Add X axis label
    g.append('text')
      .attr('y', height + margin.bottom - 15)
      .attr('x', width / 2)
      .style('text-anchor', 'middle')
      .text('Regions');
  };
  
  const actionButtons = (
    <>
      <button 
        className={`chart-type-btn ${chartType === 'pie' ? 'active' : ''}`} 
        onClick={() => setChartType('pie')}
        title="Pie Chart"
      >
        âšª
      </button>
      <button 
        className={`chart-type-btn ${chartType === 'bar' ? 'active' : ''}`} 
        onClick={() => setChartType('bar')}
        title="Bar Chart"
      >
        ðŸ“Š
      </button>
      <button 
        className="refresh-btn" 
        onClick={() => {
          setLoading(true);
          api.getRegions().then(data => {
            setData(data);
            setLoading(false);
          }).catch(err => {
            setError('Failed to reload data');
            setLoading(false);
          });
        }}
      >
        <span className="refresh-icon">â†»</span>
      </button>
    </>
  );
  
  return (
    <CardContainer title="Region Distribution" actionButtons={actionButtons}>
      {loading ? (
        <Loader message="Loading region data..." />
      ) : error ? (
        <NoDataMessage message={error} icon="âš ï¸" />
      ) : !data || data.length === 0 ? (
        <NoDataMessage message="No region data available" />
      ) : (
        <div className="chart-container">
          <svg ref={svgRef}></svg>
          <div ref={legendRef}></div>
        </div>
      )}
    </CardContainer>
  );
};

export default RegionChart;
