import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { ChevronDown } from 'lucide-react';

export const Select = ({ children }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleOpen = () => setIsOpen(!isOpen);

  return (
    <div className="relative">
      <div onClick={toggleOpen} className="cursor-pointer">
        {children}
      </div>
      {isOpen && (
        <div className="absolute z-10 mt-2 w-full bg-white border border-gray-200 rounded shadow-lg">
          {React.Children.map(children, (child) =>
            React.cloneElement(child, { isOpen, toggleOpen })
          )}
        </div>
      )}
    </div>
  );
};

Select.propTypes = {
  children: PropTypes.node.isRequired,
};

export const SelectTrigger = ({ children }) => (
  <div className="flex items-center justify-between px-4 py-2 border border-gray-300 rounded bg-white">
    <span>{children}</span>
    <ChevronDown className="h-4 w-4" />
  </div>
);

SelectTrigger.propTypes = {
  children: PropTypes.node.isRequired,
};

export const SelectContent = ({ children }) => (
  <div className="flex flex-col">
    {children}
  </div>
);

SelectContent.propTypes = {
  children: PropTypes.node.isRequired,
};

export const SelectItem = ({ value, onSelect, children }) => (
  <div
    onClick={() => onSelect(value)}
    className="px-4 py-2 cursor-pointer hover:bg-gray-100"
  >
    {children}
  </div>
);

SelectItem.propTypes = {
  value: PropTypes.string.isRequired,
  onSelect: PropTypes.func.isRequired,
  children: PropTypes.node.isRequired,
};

export const SelectValue = ({ value }) => (
  <span>{value}</span>
);

SelectValue.propTypes = {
  value: PropTypes.string.isRequired,
};
