export const sendChatMessage = async (message, customerId, role = 'guest') => {
  try {
    // Prepare the data to send to the API, including the role
    const data = {
      message,
      customer_id: customerId || 'guest', // Default to 'guest' if no customer ID
      role, // Include the role to differentiate between customers and clients
    };

    // Make the API request
    const response = await fetch('http://localhost:5000/api/chat', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    });

    // Check if the response is successful
    if (!response.ok) {
      throw new Error(`Failed to send message: ${response.statusText}`);
    }

    // Parse the JSON response from the backend
    const responseData = await response.json();

    // Return the response data containing the chatbot response, properties, and customer data
    return {
      response: responseData.response,
      properties: responseData.properties, // Includes property images, prices, descriptions
      customerData: responseData.customer_data,
      role: responseData.role, // Include role to help front-end handle specific scenarios
    };
  } catch (error) {
    console.error('Error sending chat message:', error);
    return { error: 'An error occurred while processing the chat message' };
  }
};
