// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import { getDashboardData } from '../../api/dashboardApi';
import '../../components/Dashboard/Dashboard.css';
import { Link } from 'react-router-dom';


const Dashboard = () => {
    const [dashboardData, setDashboardData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                const data = await getDashboardData();
                setDashboardData(data);
            } catch (error) {
                setError('Error fetching dashboard data');
            } finally {
                setLoading(false);
            }
        };

        fetchDashboardData();
    }, []);

    if (loading) {
        return <p>Loading dashboard...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    if (!dashboardData) {
        return <p>No dashboard data available.</p>;
    }

    return (
        <div>
            <h1>Dashboard</h1>
            <div className="dashboard-summary">
                <div className="card">
                    <h2>Total Customers</h2>
                    <p>{dashboardData.totalCustomers}</p>
                </div>
                <div className="card">
                    <h2>Total Tickets</h2>
                    <p>{dashboardData.totalTickets}</p>
                </div>
                <div className="card">
                    <h2>Total Interactions</h2>
                    <p>{dashboardData.totalInteractions}</p>
                </div>
                <div className="card">
                    <h2>Resolved Tickets</h2>
                    <p>{dashboardData.resolvedTickets}</p>
                </div>
                <div className="card">
                    <h2>Pending Tickets</h2>
                    <p>{dashboardData.pendingTickets}</p>
                </div>
            </div>
            {/* Navigation links to other sections */}
            <div className="navigation-links">
                <h3>Navigate to:</h3>
                <ul>
                    <li>
                        <Link to="/customers">Customers</Link>
                    </li>
                    <li>
                        <Link to="/analytics">Analytics</Link>
                    </li>
                    <li>
                        <Link to="/interactions">Interactions</Link>
                    </li>
                    <li>
                        <Link to="/tickets">Tickets</Link>
                    </li>
                </ul>
            </div>
        </div>
    );
};

export default Dashboard;
