// src/components/AnalyticsOverview.js
import React, { useEffect, useState } from 'react';
import { getAnalyticsOverview } from '../../api/analyticsApi';
import '../../components/Dashboard/AnalyticsOverview.css'

const AnalyticsOverview = () => {
    const [analytics, setAnalytics] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchAnalytics = async () => {
            try {
                const data = await getAnalyticsOverview();
                setAnalytics(data);
            } catch (error) {
                setError('Error fetching analytics overview');
            } finally {
                setLoading(false);
            }
        };

        fetchAnalytics();
    }, []);

    if (loading) {
        return <p>Loading analytics...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    if (!analytics) {
        return <p>No analytics data available.</p>;
    }

    return (
        <div>
            <h1>Analytics Overview</h1>
            <div className="analytics-summary">
                <div>
                    <h2>Total Customers</h2>
                    <p>{analytics.totalCustomers}</p>
                </div>
                <div>
                    <h2>Total Tickets</h2>
                    <p>{analytics.totalTickets}</p>
                </div>
                <div>
                    <h2>Total Interactions</h2>
                    <p>{analytics.totalInteractions}</p>
                </div>
                <div>
                    <h2>Resolved Tickets</h2>
                    <p>{analytics.resolvedTickets}</p>
                </div>
                <div>
                    <h2>Pending Tickets</h2>
                    <p>{analytics.pendingTickets}</p>
                </div>
            </div>
        </div>
    );
};

export default AnalyticsOverview;
