import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import * as topojson from 'topojson-client';
import api from '../../services/api';
import CardContainer from '../common/CardContainer';
import Loader from '../common/Loader';
import NoDataMessage from '../common/NoDataMessage';
import { formatNumber, createTooltip, debounce } from '../../utils/helpers';

const CountryMap = ({ metric = 'intensity', fullSize = false }) => {
  const [data, setData] = useState([]);
  const [worldData, setWorldData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedMetric, setSelectedMetric] = useState(metric);
  const [countries, setCountries] = useState([]);
  const [pestleOptions, setPestleOptions] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('all');
  const [selectedPestle, setSelectedPestle] = useState('all');
  const [regionData, setRegionData] = useState({});
  const [pestleData, setPestleData] = useState({});
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const tooltipRef = useRef(null);
  
  // Fetch filter options on initial load
  useEffect(() => {
    const fetchFilterOptions = async () => {
      try {
        const filters = await api.getFilters();
        setCountries(['all', ...filters.countries.sort()]);
        setPestleOptions(['all', ...filters.pestle.sort()]);
      } catch (err) {
        console.error('Error fetching filter options:', err);
      }
    };
    
    fetchFilterOptions();
  }, []);
  
  // Fetch region and pestle data for tooltips
  useEffect(() => {
    const fetchRegionAndPestleData = async () => {
      try {
        // Get all data to map countries to regions and pestle
        const allData = await api.getData({});
        
        // Create mappings
        const countryToRegion = {};
        const countryToPestle = {};
        
        allData.forEach(item => {
          if (item.country) {
            // Normalize country name to lowercase for comparison
            const countryKey = item.country.toLowerCase();
            
            // Map to region
            if (item.region) {
              countryToRegion[countryKey] = item.region;
            }
            
            // Map to pestle if not already set or if this pestle matches our selected filter
            if (item.pestle) {
              if (!countryToPestle[countryKey] || 
                  (selectedPestle !== 'all' && item.pestle.toLowerCase() === selectedPestle.toLowerCase())) {
                countryToPestle[countryKey] = item.pestle;
              }
            }
          }
        });
        
        setRegionData(countryToRegion);
        setPestleData(countryToPestle);
      } catch (err) {
        console.error('Error fetching region and pestle data:', err);
      }
    };
    
    fetchRegionAndPestleData();
  }, [selectedPestle]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Create filter object with selected filters
        const filters = {};
        if (selectedCountry !== 'all') filters.country = selectedCountry;
        if (selectedPestle !== 'all') filters.pestle = selectedPestle;
        
        const [topNData, worldTopoJSON] = await Promise.all([
          api.getTopN(selectedMetric, 'country', 50, filters),
          fetch('https://unpkg.com/world-atlas@2.0.2/countries-110m.json').then(res => res.json())
        ]);
        
        setData(topNData);
        setWorldData(worldTopoJSON);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching map data:', err);
        setError('Failed to load map data');
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedMetric, selectedCountry, selectedPestle]);

  useEffect(() => {
    if (data.length > 0 && worldData && !loading) {
      drawMap();
      
      // Add resize listener
      const handleResize = debounce(() => {
        drawMap();
      }, 300);
      
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }
  }, [data, worldData, loading, regionData, pestleData, selectedCountry, selectedPestle]);

  const handleMetricChange = (newMetric) => {
    setSelectedMetric(newMetric);
  };
  
  const handleCountryChange = (e) => {
    setSelectedCountry(e.target.value);
  };
  
  const handlePestleChange = (e) => {
    setSelectedPestle(e.target.value);
  };

  const drawMap = () => {
    if (!svgRef.current || !containerRef.current || !worldData) return;

    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();

    // Set dimensions
    const containerWidth = containerRef.current.clientWidth;
    const containerHeight = fullSize ? Math.min(600, containerWidth * 0.6) : Math.min(500, containerWidth * 0.6);
    const margin = { top: 20, right: 20, bottom: 20, left: 20 };
    const width = containerWidth - margin.left - margin.right;
    const height = containerHeight - margin.top - margin.bottom;

    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Create tooltip if not already created
    if (!tooltipRef.current) {
      tooltipRef.current = createTooltip(d3);
    }

    // Create map projection
    const projection = d3.geoNaturalEarth1()
      .fitSize([width, height], topojson.feature(worldData, worldData.objects.countries));

    // Create path generator
    const path = d3.geoPath().projection(projection);

    // Extract countries data
    const countries = topojson.feature(worldData, worldData.objects.countries).features;

    // Add a background rect for the ocean
    svg.append("rect")
      .attr("width", width)
      .attr("height", height)
      .attr("fill", "#E6F2F5")
      .attr("class", "ocean-background");

    // Create color scale based on metric value
    const colorScale = d3.scaleSequential(
      selectedMetric === 'intensity' ? d3.interpolateBlues :
      selectedMetric === 'likelihood' ? d3.interpolateGreens :
      d3.interpolateOranges
    ).domain([0, d3.max(data, d => d.value) || 10]);

    // Create a map for faster data lookup - normalize country names to lowercase
    const dataByCountry = {};
    data.forEach(d => {
      // Ensure country name is saved in lowercase for case-insensitive lookup
      dataByCountry[d.name.toLowerCase()] = d;
    });

    // Draw countries
    svg.selectAll('.country')
      .data(countries)
      .enter()
      .append('path')
      .attr('class', 'country')
      .attr('d', path)
      .attr('fill', d => {
        // Country name to lowercase for case-insensitive comparison
        const countryName = d.properties.name.toLowerCase();
        const countryData = dataByCountry[countryName];
        return countryData ? colorScale(countryData.value) : '#d0d0d0';
      })
      .attr('stroke', '#fff')
      .attr('stroke-width', 0.5)
      .on('mouseover', function(event, d) {
        // Country name to lowercase for case-insensitive comparison
        const countryName = d.properties.name;
        const countryLower = countryName.toLowerCase();
        const countryData = dataByCountry[countryLower];
        
        // Get region and pestle information
        const regionName = regionData[countryLower] || 'Unknown Region';
        const pestleName = pestleData[countryLower] || 'Not specified';
        
        d3.select(this)
          .transition()
          .duration(200)
          .attr('stroke', '#333')
          .attr('stroke-width', 1.5)
          .attr('filter', 'drop-shadow(0px 0px 5px rgba(0,0,0,0.3))');
        
        tooltipRef.current
          .style('opacity', 1)
          .html(`
            <div class="tooltip-header">${countryName}</div>
            <div class="tooltip-region">Region: ${regionName}</div>
            <div class="tooltip-pestle">PESTLE: ${pestleName}</div>
            ${countryData 
              ? `<div class="tooltip-content">
                  <strong>${selectedMetric.charAt(0).toUpperCase() + selectedMetric.slice(1)}:</strong> ${formatNumber(countryData.value)}<br>
                  <strong>Count:</strong> ${countryData.count} records
                </div>`
              : '<div class="tooltip-content"><i>No data available</i></div>'}
          `)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 20) + 'px');
      })
      .on('mouseout', function() {
        d3.select(this)
          .transition()
          .duration(200)
          .attr('stroke', '#fff')
          .attr('stroke-width', 0.5)
          .attr('filter', null);
        
        tooltipRef.current
          .transition()
          .duration(200)
          .style('opacity', 0);
      })
      .on('click', function(event, d) {
        const countryName = d.properties.name;
        
        // Check if the country exists in our data
        const countryExists = data.some(item => 
          item.name.toLowerCase() === countryName.toLowerCase()
        );
        
        if (countryExists) {
          // Highlight selected country
          svg.selectAll('.country').classed('selected', false);
          d3.select(this).classed('selected', true);
          
          // Set selected country in dropdown - use the exact name from the dropdown
          const matchingCountry = countries.find(c => 
            c.toLowerCase() === countryName.toLowerCase()
          );
          
          if (matchingCountry) {
            setSelectedCountry(matchingCountry);
          } else if (countryName !== 'all') {
            // If not found in dropdown but valid in data, add it
            setSelectedCountry(countryName);
          }
        }
      });

    // Add a gentle zoom effect to the map
    const zoom = d3.zoom()
      .scaleExtent([1, 8])
      .on('zoom', (event) => {
        svg.selectAll('path')
          .attr('transform', event.transform);
      });

    d3.select(svgRef.current)
      .call(zoom);

    // Add legend
    const legendWidth = 200;
    const legendHeight = 15;
    const legendPosition = {
      x: width - legendWidth - 10,
      y: height - 40
    };

    // Create gradient for legend
    const defs = svg.append('defs');
    const linearGradient = defs.append('linearGradient')
      .attr('id', `map-color-gradient-${selectedMetric}`)
      .attr('x1', '0%')
      .attr('y1', '0%')
      .attr('x2', '100%')
      .attr('y2', '0%');

    linearGradient.selectAll('stop')
      .data([
        {offset: '0%', color: colorScale(0)},
        {offset: '100%', color: colorScale(d3.max(data, d => d.value) || 10)}
      ])
      .enter().append('stop')
      .attr('offset', d => d.offset)
      .attr('stop-color', d => d.color);

    // Draw legend rectangle with rounded corners
    svg.append('rect')
      .attr('x', legendPosition.x)
      .attr('y', legendPosition.y)
      .attr('width', legendWidth)
      .attr('height', legendHeight)
      .attr('rx', 4)
      .attr('ry', 4)
      .style('fill', `url(#map-color-gradient-${selectedMetric})`)
      .style('stroke', '#ccc')
      .style('stroke-width', 0.5);

    // Add legend labels
    svg.append('text')
      .attr('x', legendPosition.x)
      .attr('y', legendPosition.y - 5)
      .attr('fill', 'var(--dark-text)')
      .attr('text-anchor', 'start')
      .attr('font-size', '10px')
      .text('Low');

    svg.append('text')
      .attr('x', legendPosition.x + legendWidth)
      .attr('y', legendPosition.y - 5)
      .attr('fill', 'var(--dark-text)')
      .attr('text-anchor', 'end')
      .attr('font-size', '10px')
      .text('High');

    svg.append('text')
      .attr('x', legendPosition.x + legendWidth / 2)
      .attr('y', legendPosition.y + 30)
      .attr('fill', 'var(--dark-text)')
      .attr('text-anchor', 'middle')
      .attr('font-size', '12px')
      .text(`${selectedMetric.charAt(0).toUpperCase() + selectedMetric.slice(1)} by Country`);
      
    // Add a subtle pulsing animation to countries with highest values
    const topCountries = data
      .sort((a, b) => b.value - a.value)
      .slice(0, 5)
      .map(d => d.name.toLowerCase());
      
    svg.selectAll('.country')
      .filter(d => topCountries.includes(d.properties.name.toLowerCase()))
      .each(function() {
        const element = d3.select(this);
        
        // Create pulsing effect
        function repeat() {
          element
            .transition()
            .duration(1000)
            .attr('stroke-width', 2)
            .attr('stroke-opacity', 1)
            .transition()
            .duration(1000)
            .attr('stroke-width', 0.5)
            .attr('stroke-opacity', 0.7)
            .on('end', repeat);
        }
        
        repeat();
      });
  };

  const getColorClass = () => {
    switch(selectedMetric) {
      case 'intensity': return 'intensity-theme';
      case 'likelihood': return 'likelihood-theme';
      case 'relevance': return 'relevance-theme';
      default: return 'intensity-theme';
    }
  };

  const renderFilterControls = () => (
    <div className="filter-controls" style={{ marginBottom: '15px', display: 'flex', gap: '15px' }}>
      <div className="filter-control">
        <label htmlFor="country-filter" style={{ marginRight: '8px', fontWeight: '500' }}>Country:</label>
        <select 
          id="country-filter" 
          value={selectedCountry} 
          onChange={handleCountryChange}
          style={{ 
            padding: '6px 10px', 
            borderRadius: '4px', 
            border: '1px solid #ddd' 
          }}
        >
          {countries.map(country => (
            <option key={country} value={country}>{country === 'all' ? 'All Countries' : country}</option>
          ))}
        </select>
      </div>
      
      <div className="filter-control">
        <label htmlFor="pestle-filter" style={{ marginRight: '8px', fontWeight: '500' }}>PESTLE:</label>
        <select 
          id="pestle-filter" 
          value={selectedPestle} 
          onChange={handlePestleChange}
          style={{ 
            padding: '6px 10px', 
            borderRadius: '4px', 
            border: '1px solid #ddd' 
          }}
        >
          {pestleOptions.map(pestle => (
            <option key={pestle} value={pestle}>{pestle === 'all' ? 'All Categories' : pestle}</option>
          ))}
        </select>
      </div>
    </div>
  );

  const renderActionButtons = () => (
    <div className="chart-actions">
      <button 
        className={`chart-action-btn ${selectedMetric === 'intensity' ? 'active' : ''}`}
        onClick={() => handleMetricChange('intensity')}
      >
        Intensity
      </button>
      <button 
        className={`chart-action-btn ${selectedMetric === 'likelihood' ? 'active' : ''}`}
        onClick={() => handleMetricChange('likelihood')}
      >
        Likelihood
      </button>
      <button 
        className={`chart-action-btn ${selectedMetric === 'relevance' ? 'active' : ''}`}
        onClick={() => handleMetricChange('relevance')}
      >
        Relevance
      </button>
    </div>
  );

  return (
    <CardContainer 
      title={`Global ${selectedMetric.charAt(0).toUpperCase() + selectedMetric.slice(1)} Distribution`}
      actionButtons={renderActionButtons()}
      className={getColorClass()}
    >
      {renderFilterControls()}
      
      <div 
        ref={containerRef} 
        style={{ 
          width: '100%', 
          minHeight: fullSize ? '600px' : '400px',
          maxHeight: fullSize ? '800px' : '500px'
        }}
        className="country-map-container"
      >
        {loading ? (
          <Loader message="Loading map data..." />
        ) : error ? (
          <NoDataMessage message={error} icon="⚠️" />
        ) : data.length === 0 ? (
          <NoDataMessage message="No country data available" />
        ) : (
          <svg 
            ref={svgRef} 
            className={`chart-svg country-map-svg ${getColorClass()}`}
            style={{
              borderRadius: '10px',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)'
            }}
          ></svg>
        )}
      </div>
      
      {!loading && data.length > 0 && (
        <div className="map-instructions">
          <small>Tip: Scroll to zoom, drag to pan, and click on countries for details</small>
        </div>
      )}
    </CardContainer>
  );
};

export default CountryMap;
