import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import api from '../../services/api';
import CardContainer from '../common/CardContainer';
import Loader from '../common/Loader';
import NoDataMessage from '../common/NoDataMessage';
import { formatNumber, truncateText, createTooltip, debounce } from '../../utils/helpers';

const TopicTreeMapChart = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const tooltipRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const topicsData = await api.getTopics();
        // Process data for treemap
        const processedData = {
          name: 'Topics',
          children: topicsData.map(topic => ({
            name: topic.topic,
            value: topic.count,
            intensity: topic.intensity || 0,
            count: topic.count || 0
          }))
        };
        setData(processedData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching topics data:', err);
        setError('Failed to load topics data');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (data.children && data.children.length > 0 && !loading) {
      drawTreeMap();
      
      // Add resize listener
      const handleResize = debounce(() => {
        drawTreeMap();
      }, 300);
      
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }
  }, [data, loading]);

  const drawTreeMap = () => {
    if (!svgRef.current || !containerRef.current) return;

    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();

    // Set dimensions
    const containerWidth = containerRef.current.clientWidth;
    const containerHeight = 500; // Fixed height for the treemap
    const legendHeight = 50;  // Height reserved for the legend
    const treemapHeight = containerHeight - legendHeight; // Adjust treemap height to make room for legend
    const margin = { top: 10, right: 10, bottom: legendHeight, left: 10 };
    const width = containerWidth - margin.left - margin.right;
    const height = treemapHeight - margin.top - margin.bottom;

    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', containerHeight)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Create tooltip if not already created
    if (!tooltipRef.current) {
      tooltipRef.current = createTooltip(d3);
    }
    
    // Create hierarchical data structure
    const root = d3.hierarchy(data)
      .sum(d => d.value)
      .sort((a, b) => b.value - a.value);
    
    // Create treemap layout
    const treemap = d3.treemap()
      .size([width, height])
      .padding(2);
    
    treemap(root);
    
    // Color scale for intensity
    const colorScale = d3.scaleLinear()
      .domain([0, d3.max(data.children, d => d.intensity) || 10])
      .range(['#8ab4db', '#1e62a7']); // Light blue to dark blue

    // Add treemap cells
    const cell = svg.selectAll('g')
      .data(root.leaves())
      .enter()
      .append('g')
      .attr('transform', d => `translate(${d.x0},${d.y0})`);
    
    // Add rectangles
    cell.append('rect')
      .attr('width', d => d.x1 - d.x0)
      .attr('height', d => d.y1 - d.y0)
      .attr('fill', d => colorScale(d.data.intensity))
      .attr('stroke', '#fff')
      .attr('stroke-width', 1)
      .on('mouseover', function(event, d) {
        d3.select(this).attr('stroke', '#333').attr('stroke-width', 2);
        tooltipRef.current
          .style('opacity', 1)
          .html(`
            <strong>Topic:</strong> ${d.data.name}<br>
            <strong>Count:</strong> ${formatNumber(d.data.count)}<br>
            <strong>Intensity:</strong> ${formatNumber(d.data.intensity)}
          `)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 20) + 'px');
      })
      .on('mouseout', function() {
        d3.select(this).attr('stroke', '#fff').attr('stroke-width', 1);
        tooltipRef.current.style('opacity', 0);
      });
    
    // Add topic labels
    cell.append('text')
      .attr('x', 5)
      .attr('y', 15)
      .attr('font-size', '12px')
      .attr('fill', d => d.data.intensity > 5 ? '#fff' : '#000')
      .text(d => truncateText(d.data.name, Math.floor((d.x1 - d.x0) / 6)));
    
    // Add count values
    cell.append('text')
      .attr('x', 5)
      .attr('y', 30)
      .attr('font-size', '10px')
      .attr('fill', d => d.data.intensity > 5 ? '#fff' : '#000')
      .text(d => {
        // Only show count if there's enough space
        return (d.x1 - d.x0 > 60 && d.y1 - d.y0 > 40) ? 
          `Count: ${formatNumber(d.data.count)}` : '';
      });
    
    // Add intensity values
    cell.append('text')
      .attr('x', 5)
      .attr('y', 45)
      .attr('font-size', '10px')
      .attr('fill', d => d.data.intensity > 5 ? '#fff' : '#000')
      .text(d => {
        // Only show intensity if there's enough space
        return (d.x1 - d.x0 > 60 && d.y1 - d.y0 > 50) ? 
          `Int: ${formatNumber(d.data.intensity)}` : '';
      });
    
    // Add legend
    const legendGroup = svg.append('g')
      .attr('transform', `translate(0, ${height + margin.top + 10})`);
      
    // Create gradient for legend
    const defs = svg.append('defs');
    const linearGradient = defs.append('linearGradient')
      .attr('id', 'intensity-gradient')
      .attr('x1', '0%')
      .attr('y1', '0%')
      .attr('x2', '100%')
      .attr('y2', '0%');
      
    linearGradient.append('stop')
      .attr('offset', '0%')
      .attr('stop-color', colorScale.range()[0]);
      
    linearGradient.append('stop')
      .attr('offset', '100%')
      .attr('stop-color', colorScale.range()[1]);
    
    // Draw legend rectangle
    const legendWidth = 200;
    legendGroup.append('rect')
      .attr('x', (width - legendWidth) / 2)
      .attr('y', 0)
      .attr('width', legendWidth)
      .attr('height', 20)
      .attr('fill', 'url(#intensity-gradient)');
      
    // Add legend labels
    legendGroup.append('text')
      .attr('x', (width - legendWidth) / 2)
      .attr('y', 35)
      .attr('text-anchor', 'start')
      .attr('font-size', '12px')
      .text('Lower Intensity');
      
    legendGroup.append('text')
      .attr('x', (width - legendWidth) / 2 + legendWidth)
      .attr('y', 35)
      .attr('text-anchor', 'end')
      .attr('font-size', '12px')
      .text('Higher Intensity');
      
    // Add legend title
    legendGroup.append('text')
      .attr('x', width / 2)
      .attr('y', -5)
      .attr('text-anchor', 'middle')
      .attr('font-size', '14px')
      .attr('font-weight', 'bold')
      .text('Topic Intensity Scale');
  };

  return (
    <CardContainer title="Topic Distribution by Intensity">
      <div ref={containerRef} style={{ width: '100%', height: '500px' }}>
        {loading ? (
          <Loader message="Loading topic data..." />
        ) : error ? (
          <NoDataMessage message={error} icon="⚠️" />
        ) : !data.children || data.children.length === 0 ? (
          <NoDataMessage message="No topic data available" />
        ) : (
          <svg ref={svgRef} className="chart-svg"></svg>
        )}
      </div>
    </CardContainer>
  );
};

export default TopicTreeMapChart;
