// src/components/ui/Tabs.js
import React, { useState } from 'react';
import '../ui/Tabs.css';
// Tabs wrapper component
const Tabs = ({ defaultValue, value, onValueChange, className = '', children }) => {
  const [localValue, setLocalValue] = useState(defaultValue);
  const currentValue = value !== undefined ? value : localValue;

  const handleValueChange = (newValue) => {
    setLocalValue(newValue);
    if (onValueChange) {
      onValueChange(newValue);
    }
  };

  // Clone children with additional props
  const enhancedChildren = React.Children.map(children, child => {
    if (!child) return null;
    
    if (child.type === TabsList || child.type === TabsContent) {
      return React.cloneElement(child, {
        value: currentValue,
        onValueChange: handleValueChange
      });
    }
    return child;
  });

  return (
    <div className={`tabs ${className}`}>
      {enhancedChildren}
    </div>
  );
};

const TabsList = ({ className = '', children, value, onValueChange }) => {
  return (
    <div className={`tabs-list ${className}`}>
      {React.Children.map(children, child => {
        if (!child) return null;
        
        if (child.type === TabsTrigger) {
          return React.cloneElement(child, {
            isSelected: child.props.value === value,
            onSelect: () => onValueChange(child.props.value)
          });
        }
        return child;
      })}
    </div>
  );
};

const TabsTrigger = ({ value, children, isSelected, onSelect, className = '' }) => {
  return (
    <button
      className={`tabs-trigger ${isSelected ? 'active' : ''} ${className}`}
      onClick={onSelect}
      type="button"
      role="tab"
      aria-selected={isSelected}
    >
      {children}
    </button>
  );
};

const TabsContent = ({ value: contentValue, children, className = '', value: selectedValue }) => {
  // Only render content if this tab's value matches the selected value
  if (contentValue !== selectedValue) {
    return null;
  }

  return (
    <div className={`tabs-content ${className}`}>
      {children}
    </div>
  );
};

export { Tabs, TabsList, TabsTrigger, TabsContent };
