import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { sendChatMessage } from '../api/chatApi';
import './Chat.css'; // We'll add some custom styles for the UI

const Chat = () => {
  const { user, customerId } = useAuth(); // Assuming customerId is available in the AuthContext
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [response, setResponse] = useState('');
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSendMessage = async () => {
    if (!message) return; // Prevent empty messages from being sent

    setLoading(true);

    // Send the message to the backend and get the response
    const result = await sendChatMessage(message, customerId);

    if (result.error) {
      setResponse(result.error);
    } else {
      // Update the chat history with the new message and response
      setChatHistory([
        ...chatHistory,
        { sender: 'user', message },
        { sender: 'bot', message: result.response },
      ]);

      // Update properties (images, prices, descriptions)
      setProperties(result.properties);

      setResponse(result.response);
    }

    setLoading(false);
    setMessage('');
  };

  return (
    <div className="chat-container">
      <h1 className="chat-header">Chat with our Assistant</h1>
      
      {/* Chat History */}
      <div className="chat-history">
        {chatHistory.map((chat, index) => (
          <div key={index} className={`chat-bubble ${chat.sender}`}>
            <p>{chat.message}</p>
          </div>
        ))}
      </div>

      {/* Input Area */}
      <div className="chat-input-area">
        <textarea
          className="chat-input"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button className="chat-send-btn" onClick={handleSendMessage} disabled={loading}>
          {loading ? 'Sending...' : 'Send'}
        </button>
      </div>

      {/* Bot Response */}
      {response && <div className="bot-response"><strong>Response:</strong> {response}</div>}

      {/* Displaying Property Information */}
      <div className="properties-suggested">
        <h2>Properties Suggested</h2>
        {properties.length > 0 ? (
          <div className="properties-list">
            {properties.map((property, index) => (
              <div key={index} className="property-card">
                <h3>{property.title}</h3>
                <p><strong>Price:</strong> ${property.price}</p>
                <p><strong>Description:</strong> {property.description}</p>
                {property.images && property.images.length > 0 && (
                  <div className="property-images">
                    <img
                      src={property.images[0]} // Displaying the first image for now
                      alt={property.title}
                      className="property-image"
                    />
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <p>No properties available based on your query.</p>
        )}
      </div>
    </div>
  );
};

export default Chat;
