// src/components/ui/Carousel.js
import React, { useState } from 'react';
import './Carousel.css';

export const Carousel = ({ children }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? React.Children.count(children) - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === React.Children.count(children) - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="carousel">
      <CarouselPrevious onClick={handlePrevious} />
      <CarouselContent>
        {React.Children.map(children, (child, index) => (
          <CarouselItem isActive={index === currentIndex}>
            {child}
          </CarouselItem>
        ))}
      </CarouselContent>
      <CarouselNext onClick={handleNext} />
    </div>
  );
};

export const CarouselContent = ({ children }) => {
  return <div className="carousel-content">{children}</div>;
};

export const CarouselItem = ({ children, isActive }) => {
  return (
    <div className={`carousel-item ${isActive ? 'active' : ''}`}>
      {children}
    </div>
  );
};

export const CarouselNext = ({ onClick }) => {
  return (
    <button className="carousel-button next" onClick={onClick}>
      &gt;
    </button>
  );
};

export const CarouselPrevious = ({ onClick }) => {
  return (
    <button className="carousel-button previous" onClick={onClick}>
      &lt;
    </button>
  );
};
