import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';
import api from '../../services/api';
import CardContainer from '../common/CardContainer';
import Loader from '../common/Loader';
import NoDataMessage from '../common/NoDataMessage';
import { createTooltip, formatNumber, debounce } from '../../utils/helpers';

const RegionChart = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartType, setChartType] = useState('radar'); // 'radar', 'bar', or 'pie'
  const [metric, setMetric] = useState('intensity'); // 'intensity', 'likelihood', or 'relevance'
  const svgRef = useRef(null);
  const legendRef = useRef(null);
  const containerRef = useRef(null);
  const tooltipRef = useRef(null);
  
  useEffect(() => {
    const fetchRegionData = async () => {
      try {
        setLoading(true);
        const regionData = await api.getRegions();
        const metricsData = await api.getMetrics('region', metric);
        
        // Merge region data with metrics
        const enhancedData = regionData.map(region => {
          const metricData = metricsData.find(m => m.region === region.region) || {};
          return {
            ...region,
            metricValue: metricData.value || 0
          };
        });
        
        setData(enhancedData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching region data:', err);
        setError('Failed to load region data');
        setLoading(false);
      }
    };
    
    fetchRegionData();
  }, [metric]);
  
  useEffect(() => {
    if (data && data.length > 0 && !loading) {
      renderChart();
      
      // Create tooltip if not already created
      if (!tooltipRef.current) {
        tooltipRef.current = createTooltip(d3);
      }
      
      // Add resize listener
      const handleResize = debounce(() => {
        renderChart();
      }, 300);
      
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }
  }, [data, loading, chartType, metric]);
  
  const renderChart = () => {
    if (!svgRef.current || !containerRef.current) return;
    
    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();
    d3.select(legendRef.current).selectAll('*').remove();
    
    const containerWidth = containerRef.current.clientWidth;
    const margin = { top: 20, right: 30, bottom: chartType === 'bar' ? 100 : 30, left: chartType === 'bar' ? 60 : 30 };
    const width = containerWidth - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;
    
    // Process and sort data
    const processedData = [...data]
      .filter(d => d.region && d.region !== 'Unknown' && d.count > 0)
      .sort((a, b) => b.count - a.count);
    
    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);
    
    // Create legend container
    const legend = d3.select(legendRef.current)
      .attr('class', 'chart-legend');
    
    if (chartType === 'radar') {
      renderRadarChart(svg, processedData, width, height, margin, tooltipRef.current, legend);
    } else if (chartType === 'pie') {
      renderPieChart(svg, processedData, width, height, margin, tooltipRef.current, legend);
    } else {
      renderBarChart(svg, processedData, width, height, margin, tooltipRef.current, legend);
    }
  };
  
  const renderRadarChart = (svg, data, width, height, margin, tooltip, legend) => {
    // Limit to top 8 regions for radar chart
    const radarData = data.slice(0, 8);
    
    // Calculate center and radius
    const centerX = width / 2;
    const centerY = height / 2;
    const radius = Math.min(centerX, centerY) * 0.8;
    
    // Features for radar chart (metrics)
    const features = ['count', 'metricValue'];
    const featureLabels = {
      'count': 'Event Count',
      'metricValue': metric.charAt(0).toUpperCase() + metric.slice(1)
    };
    
    // Angle for each feature
    const angleSlice = (Math.PI * 2) / features.length;
    
    // Scale for features
    const rScale = d3.scaleLinear()
      .domain([0, d3.max(radarData, d => Math.max(
        d.count / d3.max(radarData, d => d.count),
        d.metricValue / d3.max(radarData, d => d.metricValue)
      ))])
      .range([0, radius]);
    
    // Create circular grid
    const levels = 5;
    const gridColor = 'rgba(200, 200, 200, 0.2)';
    
    // Draw circular grid
    for (let j = 0; j < levels; j++) {
      const levelFactor = radius * ((j + 1) / levels);
      
      // Draw circular levels
      svg.append('circle')
        .attr('cx', centerX)
        .attr('cy', centerY)
        .attr('r', levelFactor)
        .attr('class', 'radar-grid')
        .style('fill', 'none')
        .style('stroke', gridColor)
        .style('stroke-dasharray', '3,3')
        .style('stroke-width', '1px');
    }
    
    // Draw axis lines
    features.forEach((feature, i) => {
      const angle = angleSlice * i - Math.PI / 2;
      const lineX = centerX + radius * Math.cos(angle);
      const lineY = centerY + radius * Math.sin(angle);
      
      svg.append('line')
        .attr('x1', centerX)
        .attr('y1', centerY)
        .attr('x2', lineX)
        .attr('y2', lineY)
        .attr('class', 'radar-axis')
        .style('stroke', gridColor)
        .style('stroke-width', '1px');
      
      // Add axis labels
      svg.append('text')
        .attr('class', 'radar-label')
        .attr('x', centerX + (radius + 20) * Math.cos(angle))
        .attr('y', centerY + (radius + 20) * Math.sin(angle))
        .attr('text-anchor', 'middle')
        .attr('dominant-baseline', 'middle')
        .style('font-size', '12px')
        .style('fill', 'var(--text-color)')
        .text(featureLabels[feature]);
    });
    
    // Color scale
    const colorScale = d3.scaleOrdinal()
      .domain(radarData.map(d => d.region))
      .range(d3.schemeCategory10);
    
    // Function to calculate path points
    const getPathCoordinates = (dataPoint) => {
      const maxCount = d3.max(radarData, d => d.count);
      const maxMetricValue = d3.max(radarData, d => d.metricValue);
      
      return features.map((feature, i) => {
        const angle = angleSlice * i - Math.PI / 2;
        
        // Normalize the value based on the feature
        let normalizedValue;
        if (feature === 'count') {
          normalizedValue = dataPoint.count / maxCount;
        } else if (feature === 'metricValue') {
          normalizedValue = dataPoint.metricValue / maxMetricValue;
        }
        
        const pointRadius = rScale(normalizedValue);
        
        return {
          x: centerX + pointRadius * Math.cos(angle),
          y: centerY + pointRadius * Math.sin(angle),
          value: feature === 'count' ? dataPoint.count : dataPoint.metricValue,
          feature
        };
      });
    };
    
    // Draw radar charts for each region
    radarData.forEach((d, i) => {
      const pathPoints = getPathCoordinates(d);
      const color = colorScale(d.region);
      
      // Draw path
      const radarLine = d3.line()
        .x(d => d.x)
        .y(d => d.y)
        .curve(d3.curveLinearClosed);
      
      // Create radar path with animation
      svg.append('path')
        .datum(pathPoints)
        .attr('class', 'radar-path')
        .attr('d', radarLine)
        .style('fill', color)
        .style('fill-opacity', 0.1)
        .style('stroke', color)
        .style('stroke-width', 2)
        .style('stroke-opacity', 0.8)
        .style('stroke-dasharray', function() { 
          return this.getTotalLength() + " " + this.getTotalLength(); 
        })
        .style('stroke-dashoffset', function() { 
          return this.getTotalLength(); 
        })
        .transition()
        .duration(1000)
        .ease(d3.easeLinear)
        .style('stroke-dashoffset', 0);
      
      // Add dots at each point
      svg.selectAll(`.radar-point-${i}`)
        .data(pathPoints)
        .enter()
        .append('circle')
        .attr('class', `radar-point-${i}`)
        .attr('cx', d => d.x)
        .attr('cy', d => d.y)
        .attr('r', 0)
        .style('fill', color)
        .transition()
        .delay(1000)
        .duration(500)
        .attr('r', 5);
        
      // Add hover effects and tooltip to dots
      svg.selectAll(`.radar-point-${i}`)
        .on('mouseover', function(event, point) {
          d3.select(this)
            .transition()
            .duration(200)
            .attr('r', 8);
          
          const featureLabel = featureLabels[point.feature];
          
          tooltip.transition()
            .duration(200)
            .style('opacity', 0.9);
          tooltip.html(`<strong>${d.region}</strong><br>${featureLabel}: ${formatNumber(point.value)}`)
            .style('left', (event.pageX + 10) + 'px')
            .style('top', (event.pageY - 28) + 'px');
        })
        .on('mouseout', function() {
          d3.select(this)
            .transition()
            .duration(200)
            .attr('r', 5);
          
          tooltip.transition()
            .duration(500)
            .style('opacity', 0);
        });
    });
    
    // Add legend
    radarData.forEach((d, i) => {
      const color = colorScale(d.region);
      
      const legendItem = legend.append('div')
        .attr('class', 'legend-item')
        .style('cursor', 'pointer')
        .on('mouseover', function() {
          // Highlight the corresponding radar path
          svg.selectAll('.radar-path')
            .style('opacity', 0.3);
          
          svg.selectAll(`.radar-point-${i}`)
            .style('opacity', 0.3);
          
          svg.selectAll('.radar-path')
            .filter((_, j) => j === i)
            .style('opacity', 1)
            .style('stroke-width', 4);
          
          svg.selectAll(`.radar-point-${i}`)
            .style('opacity', 1)
            .attr('r', 8);
        })
        .on('mouseout', function() {
          svg.selectAll('.radar-path')
            .style('opacity', 1)
            .style('stroke-width', 2);
          
          svg.selectAll(`.radar-point-${i}`)
            .style('opacity', 1)
            .attr('r', 5);
        });
      
      legendItem.append('div')
        .attr('class', 'legend-color')
        .style('background-color', color);
      
      legendItem.append('div')
        .attr('class', 'legend-text')
        .text(d.region);
    });
  };
  
  const renderPieChart = (svg, data, width, height, margin, tooltip, legend) => {
    const g = svg.append('g')
      .attr('transform', `translate(${width / 2 + margin.left}, ${height / 2 + margin.top})`);
    
    // Limit to top 10 regions for pie chart
    const pieData = data.slice(0, 10);
    
    // Calculate total for percentages
    const total = pieData.reduce((sum, d) => sum + d.count, 0);
    
    // Create color scale
    const color = d3.scaleOrdinal()
      .domain(pieData.map(d => d.region))
      .range(d3.schemeCategory10);
    
    // Create pie generator
    const pie = d3.pie()
      .value(d => d.count)
      .sort(null);
    
    // Create arc generator
    const radius = Math.min(width, height) / 2;
    const arc = d3.arc()
      .innerRadius(0)
      .outerRadius(radius * 0.8);
    
    // Create outer arc for labels
    const outerArc = d3.arc()
      .innerRadius(radius * 0.9)
      .outerRadius(radius * 0.9);
    
    // Draw pie slices with animation
    const slices = g.selectAll('path')
      .data(pie(pieData))
      .enter()
      .append('path')
      .attr('d', arc)
      .attr('fill', d => color(d.data.region))
      .attr('class', 'pie-slice')
      .attr('stroke', 'white')
      .attr('stroke-width', 2)
      .style('opacity', 0.8)
      .each(function(d) { this._current = d; }); // Store initial angles
    
    // Add slice transitions
    slices.transition()
      .duration(1000)
      .attrTween('d', function(d) {
        const interpolate = d3.interpolate({startAngle: 0, endAngle: 0}, d);
        return function(t) {
          return arc(interpolate(t));
        };
      });
    
    // Add hover effects
    slices.on('mouseover', function(event, d) {
      const percentage = (d.data.count / total * 100).toFixed(1);
      
      // Highlight slice
      d3.select(this)
        .transition()
        .duration(200)
        .attr('opacity', 1)
        .attr('stroke-width', 3)
        .attr('transform', function() {
          const centroid = arc.centroid(d);
          return `translate(${centroid[0] * 0.1}, ${centroid[1] * 0.1})`;
        });
      
      // Show tooltip
      tooltip.transition()
        .duration(200)
        .style('opacity', 0.9);
      tooltip.html(`<strong>${d.data.region}</strong><br/>
                   Count: ${formatNumber(d.data.count)}<br/>
                   Percentage: ${percentage}%<br/>
                   ${metric}: ${formatNumber(d.data.metricValue)}`)
        .style('left', (event.pageX + 10) + 'px')
        .style('top', (event.pageY - 28) + 'px');
    })
    .on('mouseout', function() {
      // Reset slice
      d3.select(this)
        .transition()
        .duration(200)
        .attr('opacity', 0.8)
        .attr('stroke-width', 2)
        .attr('transform', 'translate(0, 0)');
      
      // Hide tooltip
      tooltip.transition()
        .duration(500)
        .style('opacity', 0);
    });
    
    // Add percentage labels
    g.selectAll('text')
      .data(pie(pieData))
      .enter()
      .append('text')
      .attr('transform', d => {
        const pos = outerArc.centroid(d);
        const midAngle = d.startAngle + (d.endAngle - d.startAngle) / 2;
        pos[0] = radius * 0.95 * (midAngle < Math.PI ? 1 : -1);
        return `translate(${pos})`;
      })
      .attr('dy', '.35em')
      .style('text-anchor', d => {
        const midAngle = d.startAngle + (d.endAngle - d.startAngle) / 2;
        return midAngle < Math.PI ? 'start' : 'end';
      })
      .style('font-size', '10px')
      .style('opacity', 0)
      .text(d => {
        const percentage = (d.data.count / total * 100).toFixed(1);
        return percentage > 5 ? `${percentage}%` : '';
      })
      .transition()
      .delay(1000)
      .duration(500)
      .style('opacity', 1);
    
    // Add polylines to labels
    g.selectAll('polyline')
      .data(pie(pieData))
      .enter()
      .append('polyline')
      .style('opacity', 0)
      .style('stroke', 'var(--text-color)')
      .style('fill', 'none')
      .style('stroke-width', 1)
      .attr('points', d => {
        const pos = outerArc.centroid(d);
        const midAngle = d.startAngle + (d.endAngle - d.startAngle) / 2;
        pos[0] = radius * 0.95 * (midAngle < Math.PI ? 1 : -1);
        const percentage = (d.data.count / total * 100).toFixed(1);
        return percentage > 5 ? [arc.centroid(d), outerArc.centroid(d), pos] : null;
      })
      .transition()
      .delay(1000)
      .duration(500)
      .style('opacity', 0.4);
    
    // Create legend
    pieData.forEach((item, index) => {
      const percentage = (item.count / total * 100).toFixed(1);
      
      const legendItem = legend.append('div')
        .attr('class', 'legend-item')
        .style('cursor', 'pointer')
        .on('mouseover', function() {
          // Highlight the corresponding pie slice
          slices.filter((d, i) => i !== index)
            .transition()
            .duration(200)
            .style('opacity', 0.3);
          
          slices.filter((d, i) => i === index)
            .transition()
            .duration(200)
            .style('opacity', 1)
            .attr('transform', function(d) {
              const centroid = arc.centroid(d);
              return `translate(${centroid[0] * 0.1}, ${centroid[1] * 0.1})`;
            });
        })
        .on('mouseout', function() {
          slices.transition()
            .duration(200)
            .style('opacity', 0.8)
            .attr('transform', 'translate(0, 0)');
        });
      
      legendItem.append('div')
        .attr('class', 'legend-color')
        .style('background-color', color(item.region));
      
      legendItem.append('div')
        .attr('class', 'legend-text')
        .text(`${item.region} (${percentage}%)`);
    });
  };
  
  const renderBarChart = (svg, data, width, height, margin, tooltip, legend) => {
    const g = svg.append('g')
      .attr('transform', `translate(${margin.left}, ${margin.top})`);
    
    // Limit to top 15 regions for bar chart
    const barData = data.slice(0, 15);
    
    // Create scales
    const x = d3.scaleBand()
      .domain(barData.map(d => d.region))
      .range([0, width])
      .padding(0.2);
    
    const y = d3.scaleLinear()
      .domain([0, d3.max(barData, d => d.count) * 1.1])
      .nice()
      .range([height, 0]);
    
    // Calculate max value for the secondary metric
    const maxMetricValue = d3.max(barData, d => d.metricValue);
    
    // Create secondary y-scale for metric value
    const y2 = d3.scaleLinear()
      .domain([0, maxMetricValue * 1.1])
      .nice()
      .range([height, 0]);
    
    // Create color scale for bars
    const colorScale = d3.scaleLinear()
      .domain([0, d3.max(barData, d => d.count)])
      .range(['#4682B4', '#1E3F66']);
    
    // Create axes
    const xAxis = g.append('g')
      .attr('class', 'axis')
      .attr('transform', `translate(0, ${height})`)
      .call(d3.axisBottom(x))
      .selectAll('text')
      .attr('transform', 'rotate(-45)')
      .attr('text-anchor', 'end')
      .attr('dx', '-.8em')
      .attr('dy', '.15em');
    
    const yAxis = g.append('g')
      .attr('class', 'axis')
      .call(d3.axisLeft(y).ticks(5).tickFormat(d => formatNumber(d)));
    
    // Create secondary y-axis for metric
    const y2Axis = g.append('g')
      .attr('class', 'axis y2-axis')
      .attr('transform', `translate(${width}, 0)`)
      .call(d3.axisRight(y2).ticks(5).tickFormat(d => formatNumber(d)));
    
    // Add labels
    g.append('text')
      .attr('class', 'axis-label')
      .attr('transform', 'rotate(-90)')
      .attr('y', -margin.left + 15)
      .attr('x', -height / 2)
      .attr('dy', '1em')
      .style('text-anchor', 'middle')
      .text('Number of Events');
    
    g.append('text')
      .attr('class', 'axis-label')
      .attr('transform', 'rotate(-90)')
      .attr('y', width + margin.right - 15)
      .attr('x', -height / 2)
      .attr('dy', '1em')
      .style('text-anchor', 'middle')
      .text(metric.charAt(0).toUpperCase() + metric.slice(1));
    
    // Create grid
    g.append('g')
      .attr('class', 'grid')
      .call(d3.axisLeft(y)
        .tickSize(-width)
        .tickFormat('')
      );
    
    // Draw bars with animation
    const bars = g.selectAll('.bar')
      .data(barData)
      .enter()
      .append('rect')
      .attr('class', 'bar')
      .attr('x', d => x(d.region))
      .attr('width', x.bandwidth())
      .attr('y', height)
      .attr('height', 0)
      .attr('fill', d => colorScale(d.count))
      .on('mouseover', function(event, d) {
        // Highlight bar
        d3.select(this)
          .transition()
          .duration(200)
          .attr('opacity', 0.7)
          .attr('y', y(d.count) - 5)
          .attr('height', height - y(d.count) + 5);
        
        // Show tooltip
        tooltip.transition()
          .duration(200)
          .style('opacity', 0.9);
        tooltip.html(`
          <strong>${d.region}</strong><br/>
          Count: ${formatNumber(d.count)}<br/>
          ${metric.charAt(0).toUpperCase() + metric.slice(1)}: ${formatNumber(d.metricValue)}
        `)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 28) + 'px');
      })
      .on('mouseout', function(event, d) {
        // Reset bar
        d3.select(this)
          .transition()
          .duration(200)
          .attr('opacity', 1)
          .attr('y', y(d.count))
          .attr('height', height - y(d.count));
        
        // Hide tooltip
        tooltip.transition()
          .duration(500)
          .style('opacity', 0);
      });
    
    // Animate bars
    bars.transition()
      .duration(1000)
      .attr('y', d => y(d.count))
      .attr('height', d => height - y(d.count));
    
    // Add line for the metric value
    const line = d3.line()
      .x(d => x(d.region) + x.bandwidth() / 2)
      .y(d => y2(d.metricValue))
      .curve(d3.curveMonotoneX);
    
    const linePath = g.append('path')
      .datum(barData)
      .attr('class', 'metric-line')
      .attr('fill', 'none')
      .attr('stroke', '#ff7f0e')
      .attr('stroke-width', 3)
      .attr('d', line);
    
    // Animate line
    const totalLength = linePath.node().getTotalLength();
    linePath
      .attr('stroke-dasharray', totalLength + ' ' + totalLength)
      .attr('stroke-dashoffset', totalLength)
      .transition()
      .delay(1000)
      .duration(1500)
      .attr('stroke-dashoffset', 0);
    
    // Add dots for the line
    const dots = g.selectAll('.dot')
      .data(barData)
      .enter()
      .append('circle')
      .attr('class', 'dot')
      .attr('cx', d => x(d.region) + x.bandwidth() / 2)
      .attr('cy', d => y2(d.metricValue))
      .attr('r', 0)
      .attr('fill', '#ff7f0e');
    
    // Animate dots
    dots.transition()
      .delay(2500)
      .duration(500)
      .attr('r', 5);
    
    // Add hover effects to dots
    dots.on('mouseover', function(event, d) {
      d3.select(this)
        .transition()
        .duration(200)
        .attr('r', 8);
      
      tooltip.transition()
        .duration(200)
        .style('opacity', 0.9);
      tooltip.html(`
        <strong>${d.region}</strong><br/>
        ${metric.charAt(0).toUpperCase() + metric.slice(1)}: ${formatNumber(d.metricValue)}
      `)
        .style('left', (event.pageX + 10) + 'px')
        .style('top', (event.pageY - 28) + 'px');
    })
    .on('mouseout', function() {
      d3.select(this)
        .transition()
        .duration(200)
        .attr('r', 5);
      
      tooltip.transition()
        .duration(500)
        .style('opacity', 0);
    });
  };
  
  const actionButtons = (
    <div className="chart-actions">
      <div className="chart-type-buttons">
        <button 
          className={`chart-type-btn ${chartType === 'radar' ? 'active' : ''}`} 
          onClick={() => setChartType('radar')}
          title="Radar Chart"
        >
           Radar
        </button>
        <button 
          className={`chart-type-btn ${chartType === 'pie' ? 'active' : ''}`} 
          onClick={() => setChartType('pie')}
          title="Pie Chart"
        >
           Pie
        </button>
        <button 
          className={`chart-type-btn ${chartType === 'bar' ? 'active' : ''}`} 
          onClick={() => setChartType('bar')}
          title="Bar Chart"
        >
           Bar
        </button>
      </div>
      <div className="metric-selector">
        <select
          className="metric-select"
          value={metric}
          onChange={(e) => setMetric(e.target.value)}
        >
          <option value="intensity">Intensity</option>
          <option value="likelihood">Likelihood</option>
          <option value="relevance">Relevance</option>
        </select>
      </div>
      <button 
        className="refresh-btn" 
        onClick={() => {
          setLoading(true);
          api.getRegions().then(data => {
            setData(data);
            setLoading(false);
          }).catch(err => {
            setError('Failed to reload data');
            setLoading(false);
          });
        }}
      >
        <span className="refresh-icon"></span>
      </button>
    </div>
  );
  
  return (
    <CardContainer title="Region Distribution" actionButtons={actionButtons}>
      {loading ? (
        <Loader message="Loading region data..." />
      ) : error ? (
        <NoDataMessage message={error} icon="" />
      ) : !data || data.length === 0 ? (
        <NoDataMessage message="No region data available" />
      ) : (
        <div ref={containerRef} className="chart-container">
          <svg ref={svgRef} className="chart-svg"></svg>
          <div ref={legendRef} className="chart-legend"></div>
        </div>
      )}
    </CardContainer>
  );
};

export default RegionChart;
