import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import api from '../../services/api';
import CardContainer from '../common/CardContainer';
import Loader from '../common/Loader';
import NoDataMessage from '../common/NoDataMessage';
import { formatNumber, createTooltip, debounce } from '../../utils/helpers';

const TimeseriesChart = ({ metric = 'intensity', filters = {} }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [yearFilter, setYearFilter] = useState(filters.end_year || 'all');
  const [availableYears, setAvailableYears] = useState([]);
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const tooltipRef = useRef(null);
  const [highlightedPoint, setHighlightedPoint] = useState(null);

  // Fetch available years for the filter
  useEffect(() => {
    const fetchFilterOptions = async () => {
      try {
        const filterOptions = await api.getFilters();
        if (filterOptions && filterOptions.end_year) {
          // Sort years numerically
          const sortedYears = filterOptions.end_year.sort((a, b) => Number(a) - Number(b));
          setAvailableYears(sortedYears);
        }
      } catch (err) {
        console.error('Error fetching filter options:', err);
      }
    };

    fetchFilterOptions();
  }, []);

  // Modified data fetching with improved year filtering
useEffect(() => {
  const fetchData = async () => {
    try {
      setLoading(true);
      
      // Make a copy of filters without modifying the original
      const apiFilters = { ...filters };
      
      // Get timeseries data (without applying end_year filter in the API)
      delete apiFilters.end_year;
      const timeseriesData = await api.getTimeseries(metric, apiFilters);
      
      // Apply client-side year filtering that handles both start_year and end_year
      let filteredData = timeseriesData;
      
      if (yearFilter !== 'all') {
        filteredData = timeseriesData.filter(item => {
          const itemYear = Number(item.year);
          const filterYear = Number(yearFilter);
          
          // Check if this data point matches our year filter
          // Match if:
          // 1. The data point's year directly matches the filter year
          // 2. OR if this datapoint represents a year that falls within a range (start_year to end_year)
          //    where we would need additional data from the API to determine this
          
          return itemYear === filterYear;
        });
      }
      
      // Filter out any invalid data points
      const validData = filteredData.filter(item => 
        item.year && !isNaN(item.year) && item.value && !isNaN(item.value)
      );
      
      setData(validData);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching timeseries data:', err);
      setError('Failed to load timeseries data');
      setLoading(false);
    }
  };

  fetchData();
}, [metric, JSON.stringify(filters), yearFilter]);

  useEffect(() => {
    if (data.length > 0 && !loading) {
      drawChart();
      
      // Add resize listener
      const handleResize = debounce(() => {
        drawChart();
      }, 300);
      
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }
  }, [data, loading, highlightedPoint]);

  const getMetricColor = (metric) => {
    const colors = {
      intensity: '#3b82f6', // Blue
      likelihood: '#10b981', // Green
      relevance: '#8b5cf6'  // Purple
    };
    return colors[metric] || '#3b82f6';
  };

  const drawChart = () => {
    if (!svgRef.current || !containerRef.current) return;

    // Clear previous chart
    d3.select(svgRef.current).selectAll('*').remove();

    // Set dimensions
    const containerWidth = containerRef.current.clientWidth;
    const margin = { top: 20, right: 30, bottom: 50, left: 60 };
    const width = containerWidth - margin.left - margin.right;
    const height = 300 - margin.top - margin.bottom;

    // Create SVG
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Create tooltip if not already created
    if (!tooltipRef.current) {
      tooltipRef.current = createTooltip(d3);
    }

    // Create scales
    const xScale = d3.scaleLinear()
      .domain(d3.extent(data, d => d.year))
      .range([0, width]);

    const yScale = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.value) * 1.1])
      .range([height, 0]);

    // Create axes with minimal ticks
    const xAxis = d3.axisBottom(xScale)
      .tickFormat(d => 'Year')
      .ticks(1); // Show only the label 'Year'

    const yAxis = d3.axisLeft(yScale)
      .tickValues([0, d3.max(data, d => d.value) * 1.1]) // Only show min and max values
      .tickFormat(d => formatNumber(d));

    // Add X axis
    svg.append('g')
      .attr('class', 'axis x-axis')
      .attr('transform', `translate(0,${height})`)
      .call(xAxis)
      .selectAll('text')
      .attr('font-size', '12px')
      .attr('font-weight', '500')
      .attr('fill', '#4b5563')
      .attr('transform', 'translate(0,10)'); // Move 'Year' label down a bit

    // Add Y axis
    svg.append('g')
      .attr('class', 'axis y-axis')
      .call(yAxis)
      .selectAll('text')
      .attr('font-size', '12px')
      .attr('font-weight', '500')
      .attr('fill', '#4b5563');
    
    // Y-axis label
    svg.append('text')
      .attr('class', 'axis-label')
      .attr('transform', 'rotate(-90)')
      .attr('y', -margin.left + 15)
      .attr('x', -height / 2)
      .attr('fill', '#1f2937')
      .attr('font-size', '14px')
      .attr('font-weight', '600')
      .attr('text-anchor', 'middle')
      .text(metric.charAt(0).toUpperCase() + metric.slice(1));

    // Get the primary color for this metric
    const primaryColor = getMetricColor(metric);
    
    // Create gradient for area
    const areaGradient = svg.append('defs')
      .append('linearGradient')
      .attr('id', `areaGradient-${metric}`)
      .attr('x1', '0%')
      .attr('y1', '0%')
      .attr('x2', '0%')
      .attr('y2', '100%');
      
    areaGradient.append('stop')
      .attr('offset', '0%')
      .attr('stop-color', primaryColor)
      .attr('stop-opacity', 0.6);
      
    areaGradient.append('stop')
      .attr('offset', '100%')
      .attr('stop-color', primaryColor)
      .attr('stop-opacity', 0.05);

    // Create line generator with smooth curve
    const line = d3.line()
      .x(d => xScale(d.year))
      .y(d => yScale(d.value))
      .curve(d3.curveCardinal.tension(0.4));

    // Add area
    const area = d3.area()
      .x(d => xScale(d.year))
      .y0(height)
      .y1(d => yScale(d.value))
      .curve(d3.curveCardinal.tension(0.4));

    // Add drop shadow filter
    const filter = svg.append('defs')
      .append('filter')
      .attr('id', 'drop-shadow')
      .attr('height', '130%');

    filter.append('feGaussianBlur')
      .attr('in', 'SourceAlpha')
      .attr('stdDeviation', 3)
      .attr('result', 'blur');

    filter.append('feOffset')
      .attr('in', 'blur')
      .attr('dx', 2)
      .attr('dy', 2)
      .attr('result', 'offsetBlur');

    filter.append('feComponentTransfer')
      .append('feFuncA')
      .attr('type', 'linear')
      .attr('slope', 0.2);

    const feMerge = filter.append('feMerge');
    feMerge.append('feMergeNode')
      .attr('in', 'offsetBlur');
    feMerge.append('feMergeNode')
      .attr('in', 'SourceGraphic');

    // Add area with gradient
    svg.append('path')
      .datum(data)
      .attr('class', 'area')
      .attr('d', area)
      .attr('fill', `url(#areaGradient-${metric})`)
      .attr('opacity', 0.8);

    // Add the line with animation
    const path = svg.append('path')
      .datum(data)
      .attr('class', 'line')
      .attr('d', line)
      .attr('fill', 'none')
      .attr('stroke', primaryColor)
      .attr('stroke-width', 3)
      .attr('filter', 'url(#drop-shadow)');
    
    // Get the total length of the line for animation
    const totalLength = path.node().getTotalLength();
    
    // Animate the line on load
    path
      .attr('stroke-dasharray', totalLength + ' ' + totalLength)
      .attr('stroke-dashoffset', totalLength)
      .transition()
      .duration(1500)
      .ease(d3.easeLinear)
      .attr('stroke-dashoffset', 0);

    // Overlay for hover effects
    svg.append('rect')
      .attr('width', width)
      .attr('height', height)
      .attr('fill', 'none')
      .attr('pointer-events', 'all')
      .on('mousemove', function(event) {
        const [mouseX] = d3.pointer(event);
        const x0 = xScale.invert(mouseX);
        
        // Find the closest data point
        const bisect = d3.bisector(d => d.year).left;
        const index = bisect(data, x0);
        const d0 = data[index - 1];
        const d1 = data[index];
        
        if (!d0 || !d1) return;
        
        const d = x0 - d0.year > d1.year - x0 ? d1 : d0;
        setHighlightedPoint(d);
        
        // Update tooltip
        tooltipRef.current
          .style('opacity', 1)
          .style('left', (event.pageX + 10) + 'px')
          .style('top', (event.pageY - 20) + 'px')
          .style('background-color', 'rgba(255, 255, 255, 0.95)')
          .style('border-radius', '6px')
          .style('box-shadow', '0 4px 6px rgba(0, 0, 0, 0.1)')
          .style('padding', '10px 14px')
          .style('font-family', 'system-ui, -apple-system, sans-serif')
          .style('font-size', '14px')
          .style('border', `2px solid ${primaryColor}`)
          .html(`
            <div style="font-weight: 600; color: #1f2937; margin-bottom: 4px; border-bottom: 1px solid #e5e7eb; padding-bottom: 4px;">
              Year: ${d.year}
            </div>
            <div style="display: flex; justify-content: space-between; margin-top: 4px;">
              <span style="font-weight: 500; color: #4b5563;">${metric.charAt(0).toUpperCase() + metric.slice(1)}:</span>
              <span style="font-weight: 600; color: ${primaryColor};">${formatNumber(d.value)}</span>
            </div>
            <div style="display: flex; justify-content: space-between; margin-top: 4px;">
              <span style="font-weight: 500; color: #4b5563;">Records:</span>
              <span style="font-weight: 600; color: #1f2937;">${d.count}</span>
            </div>
          `);
      })
      .on('mouseleave', function() {
        setHighlightedPoint(null);
        tooltipRef.current.style('opacity', 0);
      });

    // Add dots with effects
    svg.selectAll('.dot')
      .data(data)
      .enter()
      .append('circle')
      .attr('class', 'dot')
      .attr('cx', d => xScale(d.year))
      .attr('cy', d => yScale(d.value))
      .attr('r', d => highlightedPoint && highlightedPoint.year === d.year ? 8 : 5)
      .attr('fill', d => highlightedPoint && highlightedPoint.year === d.year ? primaryColor : 'white')
      .attr('stroke', primaryColor)
      .attr('stroke-width', d => highlightedPoint && highlightedPoint.year === d.year ? 3 : 2)
      .style('cursor', 'pointer')
      .attr('filter', d => highlightedPoint && highlightedPoint.year === d.year ? 'url(#drop-shadow)' : 'none')
      .transition()
      .delay((d, i) => i * 50)
      .duration(500)
      .attr('opacity', 1);

    // If there's a highlighted point, add year and value labels directly on the chart
    if (highlightedPoint) {
      // Add value label above the point
      svg.append('text')
        .attr('class', 'value-label')
        .attr('x', xScale(highlightedPoint.year))
        .attr('y', yScale(highlightedPoint.value) - 15)
        .attr('text-anchor', 'middle')
        .attr('font-size', '12px')
        .attr('font-weight', 'bold')
        .attr('fill', primaryColor)
        .text(formatNumber(highlightedPoint.value));
        
      // Add year label below the point
      svg.append('text')
        .attr('class', 'year-label')
        .attr('x', xScale(highlightedPoint.year))
        .attr('y', height + 20)
        .attr('text-anchor', 'middle')
        .attr('font-size', '12px')
        .attr('font-weight', 'bold')
        .attr('fill', '#4b5563')
        .text(highlightedPoint.year);
      
      // Add vertical guide line
      svg.append('line')
        .attr('class', 'guide-line')
        .attr('x1', xScale(highlightedPoint.year))
        .attr('x2', xScale(highlightedPoint.year))
        .attr('y1', yScale(highlightedPoint.value))
        .attr('y2', height)
        .attr('stroke', primaryColor)
        .attr('stroke-width', 1.5)
        .attr('stroke-dasharray', '3,3')
        .attr('opacity', 0.7);
        
      // Add focus circle
      svg.append('circle')
        .attr('class', 'focus-ring')
        .attr('cx', xScale(highlightedPoint.year))
        .attr('cy', yScale(highlightedPoint.value))
        .attr('r', 12)
        .attr('fill', 'none')
        .attr('stroke', primaryColor)
        .attr('stroke-width', 2)
        .attr('stroke-opacity', 0.5)
        .attr('stroke-dasharray', '3,2');
    }

    // Add chart title with style
    svg.append('text')
      .attr('class', 'chart-title')
      .attr('x', width / 2)
      .attr('y', -5)
      .attr('font-size', '14px')
      .attr('font-weight', 'bold')
      .attr('text-anchor', 'middle')
      .attr('fill', '#1f2937')
      .text(`${metric.charAt(0).toUpperCase() + metric.slice(1)} Trend Analysis`);
  };

  const handleYearFilterChange = (e) => {
    setYearFilter(e.target.value);
  };

  const renderActionButtons = () => (
    <div className="chart-actions">
      <select
        className="chart-select"
        value={metric}
        onChange={(e) => window.location.href = `?metric=${e.target.value}`}
        style={{
          padding: '8px 12px',
          borderRadius: '6px',
          border: '1px solid #d1d5db',
          background: 'white',
          boxShadow: '0 1px 2px rgba(0, 0, 0, 0.05)',
          fontWeight: '500',
          color: '#374151',
          cursor: 'pointer'
        }}
      >
        <option value="intensity">Intensity</option>
        <option value="likelihood">Likelihood</option>
        <option value="relevance">Relevance</option>
      </select>
      <div className="year-filter-container" style={{ display: 'flex', alignItems: 'center' }}>
        <label 
          htmlFor="year-filter" 
          style={{ 
            marginRight: '8px',
            fontWeight: '500',
            color: '#374151' 
          }}
        >
          Year:
        </label>
        <select
          id="year-filter"
          value={yearFilter}
          onChange={handleYearFilterChange}
          style={{
            padding: '8px 12px',
            borderRadius: '6px',
            border: '1px solid #d1d5db',
            background: 'white',
            boxShadow: '0 1px 2px rgba(0, 0, 0, 0.05)',
            fontWeight: '500',
            color: '#374151',
            cursor: 'pointer'
          }}
        >
          <option value="all">All Years</option>
          {availableYears.map(year => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>
      </div>
    </div>
  );

  const chartStyles = {
    height: '100%',
    background: 'white',
    borderRadius: '8px',
    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
    overflow: 'hidden'
  };

  return (
    <CardContainer 
      title={`${metric.charAt(0).toUpperCase() + metric.slice(1)} Trend Over Time`} 
      actionButtons={renderActionButtons()}
      className="chart-card"
    >
      <div 
        ref={containerRef} 
        style={{ width: '100%', height: '350px', position: 'relative' }}
      >
        {loading ? (
          <Loader message="Loading timeseries data..." />
        ) : error ? (
          <NoDataMessage message={error} icon="⚠️" />
        ) : data.length === 0 ? (
          <NoDataMessage message="No timeseries data available" />
        ) : (
          <div style={chartStyles}>
            <svg ref={svgRef} className="chart-svg" style={{ width: '100%', height: '100%' }}></svg>
          </div>
        )}
      </div>
    </CardContainer>
  );
};

export default TimeseriesChart;
