import React, { createContext, useState, useEffect,useContext } from 'react';

// Create a Context for Auth
const AuthContext = createContext();

// Provide the AuthContext to the components that need it
const AuthProvider = ({ children }) => {
  const [authState, setAuthState] = useState(() => {
    try {
      const storedUserData = localStorage.getItem('authState');
      return storedUserData ? JSON.parse(storedUserData) : {
        isAuthenticated: false,
        customerId: null,
        userType: 'guest',
        userDetails: null,
      };
    } catch (error) {
      console.error('Failed to parse authState from localStorage:', error);
      return {
        isAuthenticated: false,
        customerId: null,
        userType: 'guest',
        userDetails: null,
      };
    }
  });

  // This function will log the user in, either as a customer or a client
  const login = (userType, customerId = null) => {
    setAuthState({
      isAuthenticated: true,
      customerId: customerId,
      userType: userType,
      userDetails: null, // Add any additional details here after authentication if necessary
    });
  };

  // This function will log the user out
  const logout = () => {
    setAuthState({
      isAuthenticated: false,
      customerId: null,
      userType: 'guest',
      userDetails: null,
    });
  };

  // Store the authentication state in local storage whenever it changes
  useEffect(() => {
    try {
      if (authState.isAuthenticated) {
        localStorage.setItem('authState', JSON.stringify(authState));
      } else {
        localStorage.removeItem('authState');
      }
    } catch (error) {
      console.error('Failed to save authState to localStorage:', error);
    }
  }, [authState]);

  return (
    <AuthContext.Provider value={{ authState, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => {
  return useContext(AuthContext);
};
// Export the AuthContext and AuthProvider
export { AuthContext, AuthProvider,useAuth};
