import React from 'react';
import { Newspaper } from 'lucide-react';

const NewsTemplate = () => {
  return (
    <div className="flex flex-col h-screen w-full bg-gray-900 text-white relative" style={{aspectRatio: '9/16'}}>
      {/* Header */}
      <div className="flex items-center justify-center bg-gray-800 p-4 gap-2">
        <Newspaper color="red" size={32} />
        <span className="text-2xl font-bold text-red-500">Simi News</span>
      </div>

      {/* Breaking News Banner */}
      <div className="bg-red-600 p-2 text-center animate-pulse">
        <span className="text-xl font-bold">ðŸ¤£ Breaking Comedy News! ðŸ¤£</span>
      </div>

      {/* News Content */}
      <div className="flex-1 p-4 space-y-6">
        <div className="bg-gray-800 rounded-lg p-4">
          <div className="text-lg">
            <span className="font-bold text-red-500">1. </span>
            Scientists Discover That 99% chora zakah bole "Somvar se suru karsyu" baanko somvar koni aave. Janah hi choriyan 16 somvar ko vrat karah!
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4">
          <div className="text-lg">
            <span className="font-bold text-red-500">2. </span>
            Latest Study Reveals That 'bas 5 min. aur' Actually Means 2 ghanta aur
          </div>
          <div className="text-sm text-gray-400 mt-2 italic">
            Scientists shocked by the discovery, but alarm clocks already knew.
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="bg-gray-800 p-2 text-center">
        <span className="text-sm text-gray-400">Swipe up for more comedy news!</span>
      </div>
    </div>
  );
};

export default NewsTemplate;
